import React, { useState } from "react";
import "../../styles/Selection.css";
import gorsel from "../../assets/image_3840.webp";
import { useNavigate } from "react-router-dom";

function CustomerLogin() {
  const navigate = useNavigate();
  const login = () => {
    navigate('/login/customer');
  }

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault(); // Burada giriÅŸ iÅŸlemi yapÄ±lacak


    try {
      const response = await fetch("http://localhost:5003/customer/login", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (data.success) {
        localStorage.setItem("user_id", data.person_id); // ðŸ”¥
        localStorage.setItem("role", data.role);         // ðŸ”¥
        navigate("/customer/panel");
      }

      else {
        setMessage(data.message);
      }
    }
    catch (err) {
      console.error("Login error:", err);
      setMessage("An error occurred during login. Please try again later.");
    }
  };

  return (
    <div className="roll-selection">
      <div className="roll-left">
        <h2> Customer Login </h2>
        <form className=" login-form" onSubmit={handleSubmit}>
          <input type="text" placeholder="UserName" value={username} onChange={(e) => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button>Login</button>
          <button type="button" onClick={login}>
            Back
          </button>
        </form>
        <p>{message}</p>
      </div>

      <div className="roll-right">
        <img src={gorsel} alt="Rol-GÃ¶rseli" />
      </div>
    </div>
  );
}

export default CustomerLogin;
