import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";

function EmployeeList() {
    const [employees, setEmployees] = useState([]);

    // API'den çalışanları çek
    useEffect(() => {
        fetch("http://localhost:5003/manager/employeeList")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setEmployees(data.employees);
                }
            })
            .catch(err => console.error("Fetch error:", err));
    }, []);

    // PanelTemplate'in rightContent kısmına tabloyu veriyoruz
    return (
        <PanelTemplate
            title="Employee List"
            rightContent={
                <div>
                    <table style={{ width: "100%", marginTop: "20px", textAlign: "center" }}>
                        <thead>
                            <tr>
                                <th>Person ID</th>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Gender</th>
                                <th>Email</th>
                                <th>Address</th>
                                <th>City</th>
                                <th>Postal Code</th>
                                <th>Phone Number</th>
                                <th>Age</th>
                                <th>Username</th>
                                <th>Hire Date</th>
                                <th>Salary</th>
                                <th>Service Duration</th>
                            </tr>
                        </thead>

                        <tbody>
                            {employees.map(emp => (
                                <tr key={emp.person_id}>
                                    <td>{emp.person_id}</td>
                                    <td>{emp.first_name}</td>
                                    <td>{emp.last_name}</td>
                                    <td>{emp.gender}</td>
                                    <td>{emp.email}</td>
                                    <td>{emp.address}</td>
                                    <td>{emp.city}</td>
                                    <td>{emp.postal_code}</td>
                                    <td>{emp.phone_number}</td>
                                    <td>{emp.age}</td>
                                    <td>{emp.username}</td>
                                    <td>{emp.hire_date}</td>
                                    <td>{emp.salary}</td>
                                    <td>{emp.service_duration}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            }
        />
    );
}

export default EmployeeList;
