import React, { useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function ProductAdd() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }

    const [formData, setFormData] = useState({
        category_id: "",
        product_type: "",
        price: "",
        material: "",
        color: "",
        size: "",       // Scarf
        length: "",     // Shawl
        stretch: ""     // Bonnet
    });

    const handleChange = (e) => {
        setFormData({
            formData, [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const response = await fetch("http://localhost:5003/product/insert", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(formData),
        });

        const data = await response.json();
        alert(data.message);
    };

    return (
        <PanelTemplate
            title="Product Add"
            rightContent={
                <form onSubmit={handleSubmit} style={{ width: "400px" }}>
                    <label>Catagory</label>
                    <options>
                        <select name="catagory_id" onChange={handleChange}>
                            <options value="">Select Category</options>
                            <option value="1">Scarf</option>
                            <option value="2">Shawl</option>
                            <option value="3">Bonnet</option>
                        </select>

                    </options>
                </form>
            }
        />
    );
}

export default ProductAdd;
