import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/Selection.css";
import gorsel from "../../assets/image_3840.webp";

function CustomerLogin() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    username: "",
    password: ""
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      const response = await fetch("http://localhost:5003/customer/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password })
      })
        .then(res => res.json())
        .then(data => {
          if (data.success) {
            // ðŸ”¥ BUNLAR ZORUNLU
            localStorage.setItem("info", JSON.stringify(data.info));
            localStorage.setItem("customer_id", data.info.person_id);

            navigate("/customer/productList");
          } else {
            alert(data.message);
          }
        });

      // âœ… Burada kritik: info ve customer_id localStorage'a yaz
      localStorage.setItem("customer_id", String(data.customer_id));
      localStorage.setItem("info", JSON.stringify(data.info));

      // âœ… Customer product list sayfasÄ±na git
      navigate("/customer/productList");

    } catch (err) {
      console.error("Login error:", err);
      setMessage("Sunucu hatasÄ±. Tekrar dene.");
    }
  };

  const goSignup = () => {
    navigate("/signup/customer");
  };

  return (
    <div className="roll-selection">
      <div className="roll-left">
        <h2>Customer Login</h2>

        <form className="login-form" onSubmit={handleSubmit}>
          <input
            name="username"
            type="text"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
          />

          <input
            name="password"
            type="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
          />

          <button type="submit">Login</button>
          <button type="button" onClick={goSignup}>Sign Up</button>
        </form>

        {message && <p>{message}</p>}
      </div>

      <div className="roll-right">
        <img src={gorsel} alt="Rol-GÃ¶rseli" />
      </div>
    </div>
  );
}

export default CustomerLogin;
