import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // URL'den ID'yi almak için
import PanelTemplate from "./PanelTemplate";

function ProductDetail() {
    const { id } = useParams(); // URL'deki productid'yi yakalar
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Backend'den tek bir ürünün detaylarını getiren API isteği
        fetch(`http://localhost:5000/api/products/${id}`)
            .then((res) => res.json())
            .then((data) => {
                setProduct(data);
                setLoading(false);
            })
            .catch((err) => console.error("Veri çekme hatası:", err));
    }, [id]);

    if (!product) return <div>Ürün bulunamadı.</div>;

    return (
        <PanelTemplate
            role="manager"
            rightContent={
                <div style={{ padding: "20px" }}>
                    <h2 style={{ borderBottom: "2px solid #eee", paddingBottom: "10px" }}>
                        Ürün Detay Paneli (Master-Detail)
                    </h2>

                    <div style={{ display: "flex", gap: "40px", marginTop: "20px", flexWrap: "wrap" }}>

                        {/* SOL TARAF: Ürün Görseli */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <img
                                src={product.image_url || "https://via.placeholder.com/400"}
                                alt={product.material}
                                style={{ width: "100%", borderRadius: "15px", boxShadow: "0 4px 15px rgba(0,0,0,0.1)" }}
                            />
                        </div>

                        {/* SAĞ TARAF: Veritabanı Detayları */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <div style={{ backgroundColor: "#f9f9f9", padding: "20px", borderRadius: "12px" }}>
                                <p><strong>Ürün ID:</strong> #{product.product_id}</p>
                                <p><strong>Kategori:</strong> {product.category_name}</p>
                                <p><strong>Materyal:</strong> {product.material}</p>
                                <p><strong>Renk:</strong> {product.color}</p>
                                <p><strong>Fiyat:</strong> <span style={{ color: "#2e7d32", fontSize: "1.2em", fontWeight: "bold" }}>{product.price} TL</span></p>
                                <p><strong>Stok Durumu:</strong> {product.quantity} Adet</p>

                                <hr />
                                <p style={{ fontSize: "0.9em", color: "#666" }}>
                                    * Bu veriler <strong>view_product_details</strong> görünümü üzerinden getirilmiştir.
                                </p>
                            </div>

                            {/* EN ALT: Sepete Ekle Butonu */}
                            <button style={{
                                marginTop: "20px",
                                width: "100%",
                                padding: "15px",
                                backgroundColor: "#ff9800",
                                color: "white",
                                border: "none",
                                borderRadius: "8px",
                                cursor: "pointer",
                                fontSize: "1.1em",
                                fontWeight: "bold"
                            }}>
                                Sepete Ekle
                            </button>
                        </div>
                    </div>
                </div>
            }
        />
    );
}

export default ProductDetail;
