                    {cart.length === 0 && <p>Sepet boş</p>}
                    {cart.map(item => (
                        <div key={item.product.product_id}>
                            <h4>{item.product.product_name}</h4>
                            <p>Fiyat: {item.product.price} ₺</p>
                            <p>Adet: {item.quantity}</p>
                        </div>
                    ))}
                </div>

            }
        />
    );
}

export default ProductCart;
