import React from "react";
import react, { useState } from "react";
import "../../styles/Selection.css";
import gorsel from "../../assets/image_3840.webp";
import { useNavigate } from "react-router-dom";

function ManagerLogin() {

  const [userName, setuserName] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const navigate = useNavigate();
  const back = () => {
    navigate(-1);
  };


  const handleSubmit = async (e) => {
    e.preventDefault(); /* Formun sayfayı yenilemesini engeller*/

    try {
      const response = await fetch("http://localhost:5000/api/manager/login", {
        method: "POST", /*POST → veri gönderiyoruz
        headlers: {"Content-Type": "application/json"}, /*headers: içerik tipinin JSON olduğunu belirtiyoruz*/
        body: JSON.stringify({ userName, password }) /*body: username ve password bilgisini JSON formatına çevirip gönderiyoruz*/
      });

      const data = await response.json(); /*Backend (Flask) JSON formatında cevap gönderir, onu çözüyoruz*/
      if (data.succes) {
        setMessage("Login Successful");

        setTimeout(() => navigate("/manager/panel"), 1000); /*1 saniye sonra yönlendirme yapar*/
      }
      else {
        setMessage(data.mesaage);
      }
    }
    catch (err) {
      console.error("Login error:", err);
      setMessage("An error occurred during login. Please try again later.");
    }
  };


  return (
    <div className="roll-selection">
      {/* Sol taraf: Login formu */}
      <div className="roll-left">
        <h2>Yönetici Login</h2>

        <form className="login-form" onSubmit={handleSubmit}>
          <input type="text" placeholder="UserName" value={userName} onchance={(e) => setName(e.target.value)} />
          {/* input değişince state güncellenir */}
          <input type="password" placeholder="Password" value={password} pnchange={(e) => setPassword(e.target.value)} />
          <button type="submit"> Login </button>
          <button type="button" onClick={back}>
            Back
          </button>
        </form>

        <p>{message}</p>
      </div>

      {/* Sağ taraf: görsel */}
      <div className="roll-right">
        <img src={gorsel} alt="Rol-Görseli" />
      </div>
    </div>
  );
}

export default ManagerLogin;
