import { useEffect, useState } from "react";
import React from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function MyOrder() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }

    useEffect(() => {
        // Login dosyasında düzelttiğimiz 'info' paketini okuyoruz
        const storedInfo = localStorage.getItem("info");
        if (storedInfo) {
            const info = JSON.parse(storedInfo);
            const pId = info.person_id;

            fetch(`http://localhost:5003/order/my-orders/${pId}`)
                .then(res => res.json())
                .then(data => {
                    if (data.success) {
                        setOrders(data.orders);
                    }
                    setLoading(false);
                })
                .catch(err => {
                    console.error("Sipariş çekme hatası:", err);
                    setLoading(false);
                });
        }
    }, []);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);


    return (
        <PanelTemplate
            title="My Order"
            rightContent={
                <div style={{ padding: "20px" }}>

                    <table style={{ width: "100%", borderCollapse: "collapse" }}>
                        <thead>
                            <tr style={{ backgroundColor: "#f4f4f4", textAlign: "left" }}>
                                <th style={{ padding: "10px" }}>Order ID</th>
                                <th style={{ padding: "10px" }}>Date</th>
                                <th style={{ padding: "10px" }}>Amount</th>
                                <th style={{ padding: "10px" }}>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders.map(order => (
                                <tr key={order.order_id} style={{ borderBottom: "1px solid #ddd" }}>
                                    <td style={{ padding: "10px" }}>#{order.order_id}</td>
                                    <td style={{ padding: "10px" }}>{order.order_date}</td>
                                    <td style={{ padding: "10px" }}>{order.total_amount} ₺</td>
                                    <td style={{ padding: "10px" }}>
                                        <span style={{ color: "green", fontWeight: "bold" }}>
                                            {order.status}
                                        </span>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {!loading && orders.length === 0 && <p>Henüz bir siparişiniz bulunmuyor.</p>}
                </div>
            }
        />
    );
}

export default MyOrder;
