import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";

function Reports() {

    const [reportData, setReportData] = useState([]); // Ana tablo verisi
    const [selectedPerson, setSelectedPerson] = useState(null); // Seçilen kişi
    const [personDetails, setPersonDetails] = useState([]); // Alt tablo verisi

    // Sayfa açıldığında View'dan verileri çek (Master)
    useEffect(() => {
        fetch("http://localhost:5000/manager/generalReport")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setReportData(data.report);
                }
            });
    }, []);

    const handlePersonClick = (personId) => {
        setSelectedPerson(personId);
        // API çağrısı: SELECT * FROM Orders WHERE ...
        fetch(`/api/person-orders/${personId}`)
            .then(res => res.json())
            .then(data => setPersonDetails(data));
    };
    return (
        <PanelTemplate
            title="Reports"
            rightContent={
                <div className="p-8">
                    <div className="flex justify-between items-center mb-6">
                        <h1 className="text-2xl font-bold">Genel İşletme Raporu</h1>
                        <button className="bg-red-600 text-white px-4 py-2 rounded">
                            PDF Olarak İndir
                        </button>
                    </div>

                    {/* ANA TABLO (MASTER) */}
                    <div className="bg-white shadow rounded-lg mb-8">
                        <table className="w-full text-left border-collapse">
                            <thead className="bg-gray-100">
                                <tr>
                                    <th className="p-3 border">Ad Soyad</th>
                                    <th className="p-3 border">Rol</th>
                                    <th className="p-3 border">İşlem Sayısı</th>
                                    <th className="p-3 border">Toplam Ciro</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reportData.map(person => (
                                    <tr
                                        key={person.person_id}
                                        onClick={() => handlePersonClick(person.person_id)}
                                        className={`cursor-pointer hover:bg-blue-50 ${selectedPerson === person.person_id ? 'bg-blue-100' : ''}`}
                                    >
                                        <td className="p-3 border">{person.ad_soyad}</td>
                                        <td className="p-3 border">{person.rol}</td>
                                        <td className="p-3 border">{person.islem_sayisi}</td>
                                        <td className="p-3 border">{person.toplam_ciro} TL</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>

                    {/* AYRINTI TABLOSU (DETAIL) */}
                    {selectedPerson && (
                        <div className="bg-gray-50 p-6 rounded-lg border border-dashed border-gray-300">
                            <h2 className="text-xl font-semibold mb-4">İşlem Detayları</h2>
                            <table className="w-full bg-white">
                                {/* Buraya seçilen kişinin sipariş detayları gelecek */}
                            </table>
                        </div>
                    )}
                </div>
            }
        />
    );
};

export default Reports;
