import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { jsPDF } from "jspdf"; // PDF için gerekli
import "jspdf-autotable";    // Tabloyu PDF'e düzgün yerleştirmek için gerekli

function Reports() {
    const [persons, setPersons] = useState([]);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [personDetails, setPersonDetails] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/manager/generalReport")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setPersons(data.report);
                }
            })
            .catch(err => console.error("Fetch error:", err));
    }, []);

    // ==========================================
    //   PDF OLUŞTURMA VE İNDİRME FONKSİYONU
    // ==========================================
    const downloadPDF = () => {
        const doc = new jsPDF('landscape'); // Geniş tablo olduğu için yatay (landscape) format

        // Başlık ekleyelim
        doc.setFontSize(18);
        doc.text("Genel Personel ve Musteri Raporu", 14, 15);
        doc.setFontSize(11);
        doc.setTextColor(100);
        doc.text(`Rapor Tarihi: ${new Date().toLocaleString()}`, 14, 22);

        // Tablo Sütunları
        const tableColumn = [
            "ID", "Ad", "Soyad", "Cinsiyet", "Email", "Sehir", "Telefon", "Yas", "Rol"
        ];

        // Tablo Verileri
        const tableRows = persons.map(p => [
            p.person_id,
            p.first_name,
            p.last_name,
            p.gender,
            p.email,
            p.city,
            p.phone_number,
            p.age,
            p.role
        ]);

        // Otomatik Tablo Oluşturma
        doc.autoTable({
            head: [tableColumn],
            body: tableRows,
            startY: 30,
            theme: 'grid', // Izgara görünümü
            headStyles: { fillColor: [47, 64, 54] }, // Senin koyu yeşil teman (#2f4036)
            styles: { fontSize: 8, cellPadding: 2 }
        });

        // PDF'i kaydet
        doc.save("Person_General_Report.pdf");
    };

    return (
        <PanelTemplate
            title="Reports"
            rightContent={
                <div style={{ width: "100%" }}>
                    <div style={{ marginBottom: "20px", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                        <h2 style={{ color: "#2f4036" }}>General Person List</h2>

                        {/* PDF BUTONU AKTİFLEŞTİRİLDİ */}
                        <button
                            type="button"
                            onClick={downloadPDF}
                            style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 25px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer",
                                fontWeight: "bold",
                                boxShadow: "0 2px 4px rgba(0,0,0,0.2)"
                            }}
                        >
                            Download PDF Report
                        </button>
                    </div>

                    <table className="orders-table" border="1">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Gender</th>
                                <th>Email</th>
                                <th>City</th>
                                <th>Phone</th>
                                <th>Age</th>
                                <th>Role</th>
                            </tr>
                        </thead>
                        <tbody>
                            {persons.map(p => (
                                <tr key={p.person_id} style={{ cursor: "pointer" }}>
                                    <td>{p.person_id}</td>
                                    <td>{p.first_name}</td>
                                    <td>{p.last_name}</td>
                                    <td>{p.gender}</td>
                                    <td>{p.email}</td>
                                    <td>{p.city}</td>
                                    <td>{p.phone_number}</td>
                                    <td>{p.age}</td>
                                    <td style={{ fontWeight: "bold" }}>{p.role}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            }
        />
    );
}

export default Reports;
