import React, { useState } from "react";
import "../../styles/Selection.css";
import gorsel from "../../assets/image_3840.webp";
import { useNavigate } from "react-router-dom";

function ManagerLogin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const navigate = useNavigate();
  const back = () => navigate(-1);

  const git = (roll) => {
    navigate(`/manager/panel/${roll}`);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5003/manager/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (data.success) {
        setMessage("Login Successful");
        setTimeout(() => navigate("/manager/panel"), 1000);
      } else {
        setMessage(data.message);
      }
    } catch (err) {
      console.error("Login error:", err);
      setMessage("An error occurred during login. Please try again later.");
    }
  };

  return (
    <div className="roll-selection">
      <div className="roll-left">
        <h2>Manager Login</h2>

        <form className="login-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={() => git("login")}>Login</button >
          <button type="button" onClick={back}>
            Back
          </button>
        </form>

        <p>{message}</p>
      </div>

      <div className="roll-right">
        <img src={gorsel} alt="Rol-GÃ¶rseli" />
      </div>
    </div>
  );
}

export default ManagerLogin;
