import React, { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom"; // Eksik olabilir, kontrol et!
import PanelTemplate from "./PanelTemplate";

function Details() {
    const { id } = useParams();
    const navigate = useNavigate();
    const location = useLocation();

    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [cart, setCart] = useState([]);

    const [info] = useState(() => {
        const stored = localStorage.getItem("info");
        return stored ? JSON.parse(stored) : {};
    });

    // ROL VE YOL MANTIĞI - Sayfa açılmıyorsa buradaki kontrolü sağlama alalım
    const role = location.pathname.includes("/manager") ? "manager" :
        location.pathname.includes("/employee") ? "employee" : "customer";

    const basePath = role === "manager" ? "/manager" :
        role === "employee" ? "/employee" : "/customer";

    const personId = info?.person_id;
    const cartkey = personId ? `cart_${personId}` : null;

    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };

    // Veri Çekme
    useEffect(() => {
        setLoading(true);
        fetch(`http://localhost:5003/product/${id}`)
            .then((res) => {
                if (!res.ok) throw new Error("Sunucu hatası: " + res.status);
                return res.json();
            })
            .then((data) => {
                setProduct(data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Veri çekme hatası:", err);
                setLoading(false);
            });
    }, [id]);

    // Sepet Yükleme
    useEffect(() => {
        if (cartkey) {
            const storedCart = JSON.parse(localStorage.getItem(cartkey)) || [];
            setCart(storedCart);
        }
    }, [cartkey]);

    const addToCart = (productToCart) => {
        if (!cartkey) {
            alert("User information could not be found, please log in again..");
            return;
        }
        let updatedCart = [...cart];
        const index = updatedCart.findIndex(item => item.product.product_id === productToCart.product_id);

        if (index !== -1) {
            if (updatedCart[index].quantity >= productToCart.quantity) {
                alert("Insufficient products in stock.!");
                return;
            }
            updatedCart[index].quantity += 1;
        } else {
            if (productToCart.quantity <= 0) {
                alert("This product is out of stock.!");
                return;
            }
            updatedCart.push({ product: productToCart, quantity: 1 });
        }
        localStorage.setItem(cartkey, JSON.stringify(updatedCart));
        setCart(updatedCart);
        alert("Product added to cart!");
    };

    if (loading) return <div style={{ padding: "20px" }}>Loading details...</div>;
    if (!product) return <div style={{ padding: "20px" }}>Product not found (ID: {id}).</div>;

    return (
        <PanelTemplate
            role={role} // Manager sayfasında manager menüsü çıkması için şart
            rightContent={
                <div style={{ padding: "20px" }}>
                    <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
                        <h2>Product Detail Panel</h2>
                        <button onClick={() => navigate(-1)} style={{ cursor: "pointer", padding: "5px 15px" }}>Back</button>
                    </div>

                    <div style={{ display: "flex", gap: "40px", flexWrap: "wrap" }}>
                        {/* GÖRSEL KISMI */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <img
                                src={product.image_url && product.image_url !== "/images/default.png"
                                    ? product.image_url
                                    : (productImages[Number(product.category_id)] || "/images/default.png")}
                                alt={product.category_name}
                                style={{ width: "100%", height: "450px", objectFit: "cover", borderRadius: "15px" }}
                                onError={(e) => { e.target.src = "/images/default.png"; }}
                            />
                        </div>

                        {/* BİLGİ KISMI */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <div style={{ backgroundColor: "#f9f9f9", padding: "20px", borderRadius: "12px" }}>
                                {(role === "manager" || role === "employee") && (
                                    <p><strong>Product ID:</strong> #{product.product_id}</p>
                                )}
                                <p><strong>Category:</strong> {product.category_name}</p>

                                {/* Kategori Bazlı Özel Bilgiler */}
                                {Number(product.category_id) === 2 && product.size && <p><strong>Size:</strong> {product.size}</p>}
                                {Number(product.category_id) === 1 && product.lengt && <p><strong>Length:</strong> {product.lengt}</p>}
                                {Number(product.category_id) === 3 && product.body && <p><strong>Body:</strong> {product.body}</p>}

                                <p><strong>Material:</strong> {product.material}</p>
                                <p><strong>Color:</strong> {product.color}</p>
                                <p><strong>Price:</strong> {product.price} TL</p>
                                <p><strong>Stock:</strong> {product.quantity} Pcs</p>
                            </div>

                            <button
                                onClick={() => addToCart(product)}
                                style={{ marginTop: "20px", width: "100%", padding: "15px", backgroundColor: "#ff9800", color: "white", border: "none", borderRadius: "8px", cursor: "pointer", fontWeight: "bold" }}
                            >
                                Add to Cart
                            </button>
                        </div>
                    </div>
                </div>
            }
        />
    );
}

export default Details;
