import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // URL'den ID'yi almak için
import PanelTemplate from "./PanelTemplate";

function Details() {
    const { id } = useParams(); // URL'deki productid'yi yakalar
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);

    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };


    // Details.js içinde
    useEffect(() => {
        fetch(`http://localhost:5003/product/${id}`) // Portu 5003 yaptık
            .then((res) => {
                if (!res.ok) throw new Error("Sunucu hatası: " + res.status);
                return res.json();
            })
            .then((data) => {
                console.log("Gelen Veri:", data);
                setProduct(data);
            })
            .catch((err) => {
                console.error("Veri çekme hatası:", err);
            });
    }, [id]);
    if (!product) return <div>Ürün bulunamadı.</div>;

    return (
        <PanelTemplate
            role="manager"
            rightContent={

                <div style={{ padding: "20px" }}>
                    <h2 style={{ borderBottom: "2px solid #eee", paddingBottom: "10px" }}>
                        Ürün Detay Paneli (Master-Detail)
                    </h2>

                    <div style={{ display: "flex", gap: "40px", marginTop: "20px", flexWrap: "wrap" }}>

                        {/* SOL TARAF: Ürün Görseli */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <img
                                src={product.image_url && product.image_url !== "/images/default.png"
                                    ? product.image_url
                                    : (productImages[Number(product.category_id)] || "/images/default.png")}
                                alt={`${product.material} ${product.category_name}`}
                                style={{
                                    width: "100%",           // Genişlik bulunduğu kabı doldursun
                                    height: "450px",         // ✅ SABİT BOYUT: Tüm resimler tam olarak bu yükseklikte olur
                                    objectFit: "cover",      // ✅ KRİTİK: Resmi yamultmadan alanı dolduracak şekilde kırpar
                                    borderRadius: "15px",    // Köşeleri yumuşatır
                                    boxShadow: "0 4px 15px rgba(0,0,0,0.1)", // Şık bir gölge ekler
                                    display: "block"
                                }}
                                onError={(e) => { e.target.src = "/images/default.png"; }}
                            />
                        </div>

                        {/* SAĞ TARAF: Veritabanı Detayları */}
                        // ... (üst kısımlar aynı kalacak)

                        {/* SAĞ TARAF: Veritabanı Detayları */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <div style={{ backgroundColor: "#f9f9f9", padding: "20px", borderRadius: "12px" }}>
                                <p><strong>Ürün ID:</strong> #{product.product_id}</p>
                                <p><strong>Kategori:</strong> {product.category_name}</p>

                                {/* --- KATEGORİYE ÖZEL ALANLAR --- */}

                                {/* Eğer Kategori Eşarp (ID: 2) ise Boyut göster */}
                                {Number(product.category_id) === 2 && product.size && (
                                    <p><strong>Ebat:</strong> {product.size}</p>
                                )}

                                {/* Eğer Kategori Şal (ID: 1) ise Uzunluk göster */}
                                {Number(product.category_id) === 1 && product.lengt && (
                                    <p><strong>Uzunluk:</strong> {product.lengt}</p>
                                )}

                                {/* Eğer Kategori Bone (ID: 3) ise Beden/Tip göster */}
                                {Number(product.category_id) === 3 && product.body && (
                                    <p><strong>Beden:</strong> {product.body}</p>
                                )}

                                {/* ------------------------------ */}

                                <p><strong>Materyal:</strong> {product.material}</p>
                                <p><strong>Renk:</strong> {product.color}</p>
                                <p><strong>Fiyat:</strong> <span style={{ color: "#2e7d32", fontSize: "1.2em", fontWeight: "bold" }}>{product.price} TL</span></p>
                                <p><strong>Stok Durumu:</strong> {product.quantity} Adet</p>

                                <hr />
                                <p style={{ fontSize: "0.9em", color: "#666" }}>
                                    * Bu veriler <strong>view_product_details</strong> üzerinden getirilmiştir.
                                </p>
                            </div>

                            {/* Sepete Ekle Butonu */}
                            <button style={{
                                marginTop: "20px",
                                width: "100%",
                                padding: "15px",
                                backgroundColor: "#ff9800",
                                color: "white",
                                border: "none",
                                borderRadius: "8px",
                                cursor: "pointer",
                                fontSize: "1.1em",
                                fontWeight: "bold"
                            }}>
                                Sepete Ekle
                            </button>
                        </div>

// ... (alt kısımlar aynı kalacak)
                    </div>
                </div>
            }
        />
    );
}

export default Details;
