import React, { useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function Account() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }
    const [info, setInfo] = React.useState({});

    // Bilgilerin Ã§ekildiÄŸi sayfadaki useEffect (Ã–rnek)
    useEffect(() => {
        const personId = localStorage.getItem("person_id"); // âœ… doÄŸru key

        if (!personId || personId === "null" || personId === "undefined") {
            console.error("person_id bulunamadÄ± -> login sayfasÄ±na yÃ¶nlendiriliyor");
            navigate("/login/customer"); // âœ… senin route'una gÃ¶re dÃ¼zelt
            return;
        }

        fetch(`http://localhost:5003/customer/info/${personId}`)
            .then((res) => res.json())
            .then((data) => {
                if (data.success) {
                    // Backend cevap alan adÄ±na gÃ¶re: data.user veya data.info olabilir
                    setInfo(data.user || data.info);
                } else {
                    console.error("info error:", data.message);
                }
            })
            .catch((err) => console.error("fetch error:", err));
    }, [navigate]);

    if (!info) return <p>Loading...</p>;

    return (
        <PanelTemplate
            title="My Account"
            rightContent={
                <div>
                    <p><b>First Name: </b>{info.first_name}</p>
                    <p><b>Last Name: </b>{info.last_name}</p>
                    <p><b>Gender: </b>{info.gender}</p>
                    <p><b>Email: </b>{info.email}</p>

                    {/* ðŸ”¥ DÄ°KKAT: Backend'den 'adress' (tek 's') olarak geliyor olabilir */}
                    <p><b>Address: </b>{info.adress || info.address}</p>

                    <p><b>City : </b>{info.city}</p>
                    <p><b>Post Code: </b>{info.postcode}</p> {/* Backend'de 'postcode' demiÅŸtin */}
                    <p><b>Phone: </b>{info.phone || info.phone_number}</p>
                    <p><b>Age: </b>{info.age}</p>
                    <p><b>UserName: </b>{info.username}</p>


                </div>


            }
        />
    );
}

export default Account;
