import React, { useState } from "react";
import "../../styles/Selection.css";
import gorsel from "../../assets/image_3840.webp";
import { useNavigate } from "react-router-dom";

function CustomerSignUp() {

  const navigate = useNavigate();
  const back = () => {
    navigate(-1);
  };
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    username: "",
    password: ""
  });
  const [message, setMessage] = useState("");
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };
  const handleSubmit = async (e) => {
    e.preventDefault(); // Burada kayıt işlemi yapılacak

    try {
      const response = await fetch("http://localhost:5003/customer/signup", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      setMessage(data.message);
    }
    catch (err) {
      console.error("Signup error:", err);
      setMessage("An error occurred during signup. Please try again later.");
    }

    return (
      <div className="roll-selection">
        <div className="roll-left">
          <h2>Sign Up </h2>
          <form className="login-form">
            <input type="text" placeholder="first_name" onChange={handleSubmit} />
            <input type="text" placeholder="last_name" onChange={handleSubmit} />
            <input type="text" placeholder="email" onChange={handleSubmit} />
            <input type="text" placeholder="Username" onChange={handleSubmit} />
            <input type="password" placeholder="Password" onChange={handleSubmit} />
            <button> Login </button>
            <button type="button" onClick={back}>
              Back
            </button>
          </form>
          <p>{message}</p>
        </div>

        <div className="roll-right">
          <img src={gorsel} alt="Rol-Görseli" />
        </div>
      </div>
    );
  }
}

export default CustomerSignUp;
