import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate, useLocation } from "react-router-dom";
import SplashScreen from './SplashScreen';

function ProductList() {
    const navigate = useNavigate();
    const location = window.location;
    const [loading, setLoading] = useState(true);
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState([]);

    const [info, setInfo] = useState(() => {
        const stored = localStorage.getItem("info");
        return stored ? JSON.parse(stored) : {};
    });

    const role = location.pathname.startsWith("/manager") ? "manager" : location.pathname.startsWith("/employee") ? "employee" : "customer";

    const basePath = role === "manager" ? "/manager" : role === "employee" ? "/employee" : "/customer";

    const git = (page) => {
        navigate(`${basePath}/productList/${page}`)
    }


    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setProducts(data.products);
                }
                // Veriler geldikten sonra splash ekranÄ±nÄ± kapat (hafif bir gecikme ÅŸÄ±k durur)
                setTimeout(() => {
                    setLoading(false);
                }, 1000);
            })
            .catch(err => {
                console.error("YÃ¼kleme hatasÄ±:", err);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <SplashScreen text="ÃœrÃ¼nler HazÄ±rlanÄ±yor..." />;
    }


    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };



    const handleDelete = async (id) => {
        if (!window.confirm("Bu Ã¼rÃ¼nÃ¼ silmek istediÄŸine emin misin?")) return;

        try {
            const response = await fetch(
                `http://localhost:5003/product/delete/${id}`,
                { method: "DELETE" }
            );

            const data = await response.json();

            if (data.success) {
                alert("Product deleted!");
                setProducts(prev =>
                    prev.filter(p => p.product_id !== id)
                );
            } else {
                alert(data.message);
            }
        } catch (err) {
            console.error("Delete error:", err);
            alert("Error while deleting product.");
        }
    };



    const personId = info?.person_id; // info.username deÄŸil, person_id kullanÄ±yoruz
    const cartkey = personId ? `cart_${personId}` : null;

    /* ðŸ”¹ Sayfa aÃ§Ä±lÄ±nca / kullanÄ±cÄ± deÄŸiÅŸince sepeti oku */
    useEffect(() => {
        if (!cartkey) {
            setCart([]);
            return;
        }

        const storedCart = JSON.parse(localStorage.getItem(cartkey)) || [];
        setCart(storedCart);
    }, [cartkey]);

    /* ðŸ”¹ Sepete ekle (stok kontrollÃ¼) */
    const addToCart = (product) => {
        // ðŸ”¥ cartkey burada 'cart_23' (ID) formatÄ±nda olmalÄ±
        if (!cartkey) {
            alert("KullanÄ±cÄ± bilgisi bulunamadÄ±, lÃ¼tfen tekrar giriÅŸ yapÄ±n.");
            return;
        }

        let updatedCart = [...cart];
        const index = updatedCart.findIndex(
            item => item.product.product_id === product.product_id
        );

        if (index !== -1) {
            if (updatedCart[index].quantity >= product.quantity) {
                alert("Stokta yeterli Ã¼rÃ¼n yok!");
                return;
            }
            updatedCart[index].quantity += 1;
        } else {
            if (product.quantity <= 0) {
                alert("Bu Ã¼rÃ¼n stokta yok!");
                return;
            }
            updatedCart.push({ product, quantity: 1 });
        }

        // DoÄŸru kullanÄ±cÄ±ya Ã¶zel ID'li anahtarla kaydet
        localStorage.setItem(cartkey, JSON.stringify(updatedCart));
        setCart(updatedCart);
    };
    /* ðŸ”¹ Sepet sayacÄ± */
    const totalCount = cart.reduce(
        (sum, item) => sum + item.quantity,
        0
    );





    return (
        <PanelTemplate
            title="Product List"
            rightContent={
                <div>
                    {role == "manager" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    {role == "employee" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    {role == "customer" && (
                        <div style={{ display: "flex", justifyContent: "flex-end" }}>

                            <button
                                onClick={() => git("account")}
                                style={{
                                    backgroundColor: "#538456ff",
                                    color: "white",
                                    padding: "10px 20px",
                                    borderRadius: "8px",
                                    border: "none",
                                    cursor: "pointer"
                                }}
                            >
                                {info?.username}
                            </button>

                            <button
                                onClick={() => git("productCart")}
                                style={{
                                    backgroundColor: "#538456ff",
                                    color: "white",
                                    padding: "10px 20px",
                                    borderRadius: "8px",
                                    border: "none",
                                    cursor: "pointer"
                                }}
                            >
                                Cart ({totalCount})
                            </button>

                        </div>

                    )}
                    <div className="product-grid">
                        {products.map(p => (
                            <div className="product-card" key={p.product_id}>

                                {/* MANAGER GÃ–RÃœNÃœMÃœ */}
                                {role === "manager" && (
                                    <div style={{ cursor: "pointer" }}>
                                        <img
                                            src={p.image_url && p.image_url !== "/images/default.png" ? p.image_url : (productImages[p.category_id] || "/images/default.png")}
                                            alt={p.product_name}
                                            className="product-cart"
                                            onError={(e) => { e.target.src = "/images/default.png"; }}
                                        />
                                        <h3>{p.product_name}</h3>
                                        <p>{p.material}</p>
                                        <p>{p.price} â‚º</p>
                                        <button type="button" onClick={() => handleDelete(p.product_id)} style={{ backgroundColor: "#cd2014ff", color: "white", padding: "10px 20px", borderRadius: "8px", border: "none", cursor: "pointer" }}>
                                            Delete
                                        </button>
                                    </div>
                                )}

                                {/* EMPLOYEE GÃ–RÃœNÃœMÃœ */}
                                {role === "employee" && (
                                    <>
                                        <div style={{ cursor: "pointer" }}>
                                            <img
                                                src={p.image_url && p.image_url !== "/images/default.png" ? p.image_url : (productImages[p.category_id] || "/images/default.png")}
                                                alt={p.product_name}
                                                className="product-image"
                                                onError={(e) => { e.target.src = "/images/default.png"; }}
                                            />
                                            <h3>{p.product_name}</h3>
                                            <p>{p.material}</p>
                                            <p>{p.price} â‚º</p>
                                        </div>
                                        <button type="button" onClick={() => handleDelete(p.product_id)} style={{ backgroundColor: "#cd2014ff", color: "white", padding: "10px 20px", borderRadius: "8px", border: "none", cursor: "pointer" }}>
                                            Delete
                                        </button>
                                    </>
                                )}

                                {/* CUSTOMER GÃ–RÃœNÃœMÃœ */}
                                {role === "customer" && (
                                    <>
                                        <div style={{ cursor: "pointer" }}>
                                            <img
                                                src={p.image_url && p.image_url !== "/images/default.png" ? p.image_url : (productImages[p.category_id] || "/images/default.png")}
                                                alt={p.product_name}
                                                style={{ width: "100%", height: "200px", objectFit: "cover" }}
                                                onError={(e) => { e.target.src = "/images/default.png"; }}
                                            />
                                            <h3>{p.product_name}</h3>
                                            <p>{p.material}</p>
                                            <p>{p.price} â‚º</p>
                                        </div>
                                        <button type="button" onClick={() => addToCart(p)} style={{ padding: "10px", backgroundColor: "#538456ff", color: "white", border: "none", borderRadius: "8px", cursor: "pointer" }}>
                                            Add to Cart
                                        </button>
                                    </>
                                )}
                            </div>
                        ))}
                    </div>
                </ div>

            }
        />
    );
}

export default ProductList;
