import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";

function ProductCart() {
    const [cart, setCart] = useState([]);
    const [info, setInfo] = useState(null);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setProducts(data.products);
                }
            });
    }, []);


    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };


    useEffect(() => {
        const storedInfo = localStorage.getItem("info");
        if (storedInfo) {
            setInfo(JSON.parse(storedInfo));
        }
    }, []);

    /* ðŸ”¹ username & cartKey */
    const username = info?.username;
    const cartkey = username ? `cart_${username}` : null;


    useEffect(() => {
        if (!info?.username) return;

        const cartKey = `cart_${info.username}`;
        const storedCart = JSON.parse(localStorage.getItem(cartKey)) || [];
        setCart(storedCart);
    }, [info]);

    const decreaseQuantity = (productId) => {
        if (!cartkey) return;

        const updatedCart = cart
            .map(item =>
                item.product.product_id === productId
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            )
            .filter(item => item.quantity > 0);

        localStorage.setItem(cartkey, JSON.stringify(updatedCart));
        setCart(updatedCart);
    };


    const clearCart = () => {
        if (!cartkey) return;

        localStorage.removeItem(cartkey);
        setCart([]);
    };

    const placeOrder = async () => {
        // 1. localStorage'dan bilgileri alÄ±rken key ismine dikkat
        // EÄŸer daha Ã¶nce 'user_details' olarak kaydettiysen onu Ã§ekmelisin
        const info = JSON.parse(localStorage.getItem("info")) ||
            JSON.parse(localStorage.getItem("user_details"));

        const userId = localStorage.getItem("user_id");

        if (!userId) {
            alert("KullanÄ±cÄ± bilgisi bulunamadÄ±, lÃ¼tfen tekrar giriÅŸ yapÄ±n.");
            return;
        }

        const username = info?.username || localStorage.getItem("username");
        const cartKey = `cart_${username}`;
        const cart = JSON.parse(localStorage.getItem(cartKey)) || [];

        if (cart.length === 0) {
            alert("Sepetiniz boÅŸ.");
            return;
        }

        const totalAmount = cart.reduce(
            (sum, item) => sum + Number(item.product.price) * item.quantity,
            0
        );

        // Backend'in beklediÄŸi formatta paketi hazÄ±rla
        const orderData = {
            customer_id: userId, // Backend'de person_id Ã¼zerinden customer_id bulan koda gider
            total_amount: totalAmount,
            items: cart.map(item => ({
                product_id: item.product.product_id,
                quantity: item.quantity,
                price: Number(item.product.price) // 'unit_price' deÄŸil 'price' olmalÄ±
            }))
        };

        try {
            const response = await fetch("http://localhost:5003/order/create", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(orderData)
            });

            // Hata varsa detayÄ±nÄ± konsola yazdÄ±r ki gÃ¶rebilelim
            if (!response.ok) {
                const errorData = await response.json();
                console.error("Backend HatasÄ±:", errorData);
                throw new Error(errorData.message || "Sunucu sipariÅŸi kabul etmedi");
            }

            const data = await response.json();

            if (data.success) {
                alert("SipariÅŸ alÄ±ndÄ±, onay bekliyor!");
                localStorage.removeItem(cartKey);
                setCart([]); // State'i temizle
            } else {
                alert(data.message || "SipariÅŸ oluÅŸturulamadÄ±");
            }

        } catch (err) {
            console.error("Order error:", err);
            alert("Hata: " + err.message);
        }
    };



    return (
        <PanelTemplate
            title="Product Cart"
            rightContent={

                <div className="cart-layout">
                    {/* SOL TARAF â€“ ÃœRÃœNLER */}
                    <div className="cart-products">
                        {cart.length === 0 && <p>Sepet boÅŸ</p>}
                        <div className="product-gridcart">
                            {cart.map(item => (
                                <div className="product-card" key={item.product.product_id}>
                                    <img
                                        src={
                                            productImages[item.product.category_id]
                                            || "/images/default.png"
                                        }
                                        alt="Product"
                                        className="product-image"
                                    />
                                    <h3>{item.product.product_name}</h3>
                                    <p>{item.product.material}</p>
                                    <p>{item.product.price} â‚º</p>
                                    <p>Adet: {item.quantity}</p>

                                    <button
                                        onClick={() =>
                                            decreaseQuantity(item.product.product_id)
                                        }>
                                        Adet Azalt
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* SAÄž TARAF â€“ Ã–DEME */}
                    <div className="cart-payment">
                        <h3>Summary</h3>
                        <p>
                            Sum Product: {" "}
                            {cart.reduce((sum, i) => sum + i.quantity, 0)}
                        </p>
                        <p>
                            Total Amount: {" "}
                            {cart.reduce((sum, i) => sum + i.quantity * Number(i.product.price), 0)} â‚º
                        </p>

                        {cart.length > 0 && (
                            <>
                                <button className="pay-btn" onClick={placeOrder}>
                                    Proceed to payment
                                </button>
                                <button className="clear-btn" onClick={clearCart}>
                                    Clear Cart
                                </button>
                            </>
                        )}
                    </div>
                </ div>
            }
        />
    );
}

export default ProductCart;
