import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import jsPDF from 'jspdf';
import 'jspdf-autotable';

function Reports() {
    const [reportData, setReportData] = useState([]);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [personDetails, setPersonDetails] = useState([]);

    // Sayfa yüklendiğinde Ana Listeyi (Master) çek
    useEffect(() => {
        fetch("http://localhost:5000/manager/generalReport")
            .then(res => res.json())
            .then(data => { if (data.success) setReportData(data.report); });
    }, []);

    // Satıra tıklandığında Detayları (Detail) çek
    const handlePersonClick = (personId) => {
        setSelectedPerson(personId);
        fetch(`http://localhost:5000/manager/person-orders/${personId}`)
            .then(res => res.json())
            .then(data => { if (data.success) setPersonDetails(data.orders); });
    };

    // PDF Oluşturma Fonksiyonu
    const downloadPDF = () => {
        const doc = new jsPDF();
        doc.text("Genel Isletme Raporu", 14, 15);
        const rows = reportData.map(p => [p.ad_soyad, p.rol, p.islem_sayisi, `${p.toplam_ciro} TL`]);
        doc.autoTable({
            head: [['Ad Soyad', 'Rol', 'Islem Sayisi', 'Toplam Ciro']],
            body: rows,
            startY: 20
        });
        doc.save("isletme_raporu.pdf");
    };

    return (
        <PanelTemplate title="Manager Reports" rightContent={
            <div className="p-6">
                <div className="flex justify-between items-center mb-6">
                    <h1 className="text-2xl font-bold text-gray-800">Genel Isletme Raporu</h1>
                    <button onClick={downloadPDF} className="bg-red-600 hover:bg-red-700 text-white px-6 py-2 rounded-lg font-medium transition">
                        PDF Olarak Indir
                    </button>
                </div>

                {/* ANA TABLO (MASTER) */}
                <div className="bg-white shadow-md rounded-xl overflow-hidden mb-10 border">
                    <table className="w-full text-left">
                        <thead className="bg-gray-800 text-white">
                            <tr>
                                <th className="p-4">Ad Soyad</th>
                                <th className="p-4">Rol</th>
                                <th className="p-4">Islem Sayisi</th>
                                <th className="p-4">Toplam Ciro</th>
                            </tr>
                        </thead>
                        <tbody>
                            {reportData.map(person => (
                                <tr key={person.person_id} onClick={() => handlePersonClick(person.person_id)}
                                    className={`border-b cursor-pointer transition ${selectedPerson === person.person_id ? 'bg-blue-100' : 'hover:bg-gray-50'}`}>
                                    <td className="p-4 font-medium">{person.ad_soyad}</td>
                                    <td className="p-4 text-gray-600">{person.rol}</td>
                                    <td className="p-4">{person.islem_sayisi}</td>
                                    <td className="p-4 font-bold">{person.toplam_ciro} TL</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* AYRINTI TABLOSU (DETAIL) */}
                {selectedPerson && (
                    <div className="bg-gray-50 p-6 rounded-xl border-2 border-dashed border-gray-300 animate-fadeIn">
                        <h2 className="text-xl font-bold mb-4 text-blue-800">Secili Kisinin Islem Detaylari</h2>
                        <table className="w-full bg-white rounded-lg shadow-sm">
                            <thead className="bg-blue-600 text-white">
                                <tr>
                                    <th className="p-3">Siparis ID</th>
                                    <th className="p-3">Tarih</th>
                                    <th className="p-3">Tutar</th>
                                </tr>
                            </thead>
                            <tbody>
                                {personDetails.map(order => (
                                    <tr key={order.order_id} className="border-b text-center">
                                        <td className="p-3 font-mono">{order.order_id}</td>
                                        <td className="p-3">{order.order_date}</td>
                                        <td className="p-3 font-bold">{order.total_amount} TL</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>
        } />
    );
}

export default Reports;
