import React, { useEffect, useState } from "react"; // useState'i yukarıdan import etmek daha iyidir
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function Account() {
    const navigate = useNavigate();
    const [info, setInfo] = useState(null); // Başlangıç değerini null yapalım ki loading kontrolü çalışsın

    useEffect(() => {
        const personId = localStorage.getItem("person_id");

        if (!personId || personId === "null" || personId === "undefined") {
            navigate("/login/customer");
            return;
        }

        // Backend portun 5003 mü? (Genelde 5000 olur, 5003 ise doğru kalsın)
        fetch(`http://localhost:5003/customer/info/${personId}`)
            .then((res) => res.json())
            .then((data) => {
                if (data.success) {
                    // Backend'de "info" anahtarı altında gönderiyoruz
                    setInfo(data.info);
                } else {
                    console.error("Bilgi çekme hatası:", data.message);
                }
            })
            .catch((err) => console.error("Sunucu bağlantı hatası:", err));
    }, [navigate]);

    // Veri henüz gelmediyse veya null ise
    if (!info) return (
        <PanelTemplate title="My Account" rightContent={<p>Loading...</p>} />
    );

    return (
        <PanelTemplate
            title="My Account"
            rightContent={
                <div style={{ lineHeight: '2' }}>
                    <p><b>First Name: </b>{info.first_name}</p>
                    <p><b>Last Name: </b>{info.last_name}</p>
                    <p><b>Gender: </b>{info.gender}</p>
                    <p><b>Email: </b>{info.email}</p>
                    <p><b>Address: </b>{info.address}</p> {/* Çift 'd', çift 's' - veritabanındaki gibi */}
                    <p><b>City: </b>{info.city}</p>
                    <p><b>Post Code: </b>{info.postal_code}</p> {/* 'postal_code' olmalı */}
                    <p><b>Phone: </b>{info.phone_number}</p> {/* 'phone_number' olmalı */}
                    <p><b>Age: </b>{info.age}</p>
                    <p><b>UserName: </b>{info.username}</p>

                    <hr />
                    <p><b>Customer Type: </b>{info.customer_type}</p>
                    <p><b>Credit: </b>{info.credit_amount} TL</p>
                </div>
            }
        />
    );
}

export default Account;
