import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function Orders() {

    const [orders, setOrders] = useState([]);

    const role = localStorage.getItem("role");
    let approvedById = null;

    if (role === "employee") {
        approvedById = localStorage.getItem("employee_id");
    } else if (role === "manager") {
        approvedById = localStorage.getItem("manager_id");
    }


    useEffect(() => {
        fetch("http://localhost:5003/order/pending")
            .then(res => res.json())
            .then(data => {
                if (data.success)
                    setOrders(data.orders);
            });
    }, []);



    const approveOrder = async (pendingOrderId) => {
        const role = localStorage.getItem("role"); // EMPLOYEE / MANAGER
        const userId = localStorage.getItem("user_id");

        await fetch(`http://localhost:5003/order/approve/${pendingOrderId}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                approved_by_id: userId,
                approved_by_role: role
            })
        });
    };


    return (
        <PanelTemplate
            title="Orders"
            rightContent={
                <div>
                    {orders.length === 0 && (<p>No pending orders.</p>)}

                    <table border="1" style={{ width: "100%", marginTop: "20px", textAlign: "center" }}>
                        <thead>
                            <tr>
                                <th>Pending Order ID</th>
                                <th>customer ID</th>
                                <th>Order Date</th>
                                <th>Total Amount</th>
                            </tr>
                        </thead>

                        <tbody>
                            {orders.map(order => (
                                <tr key={order.pending_order_id}>
                                    <td>{order.pending_order_id}</td>
                                    <td>{order.customer_id}</td>
                                    <td>{order.order_date}</td>
                                    <td>{order.total_amount}</td>

                                    <td>
                                        <button onClick={() => approveOrder(order.pending_order_id)}
                                            style={{
                                                backgroundColor: "#c50b0bff",
                                                color: "white",
                                                padding: "8px 20px",
                                                borderRadius: "8px",
                                                border: "none",
                                                cursor: "pointer"
                                            }}
                                        >
                                            Onayla
                                        </button>
                                    </td>
                                </tr>

                            ))}
                        </tbody>
                    </table>

                </div >
            }
        />
    );
}

export default Orders;
