import React, { useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function Account() {
    const navigate = useNavigate();
    const [info, setInfo] = React.useState(null); // BaÅŸlangÄ±Ã§ta null olmasÄ± kontrol iÃ§in daha iyidir

    useEffect(() => {
        const personId = localStorage.getItem("person_id");

        if (!personId || personId === "null" || personId === "undefined") {
            console.error("person_id bulunamadÄ± -> login sayfasÄ±na yÃ¶nlendiriliyor");
            navigate("/login/customer");
            return;
        }

        fetch(`http://localhost:5003/customer/info/${personId}`)
            .then((res) => res.json())
            .then((data) => {
                if (data.success) {
                    // Backend'de "return jsonify({"success": True, "info": {...}})" yazdÄ±ÄŸÄ±n iÃ§in:
                    setInfo(data.info);
                } else {
                    console.error("Bilgi getirme hatasÄ±:", data.message);
                }
            })
            .catch((err) => console.error("Sunucu hatasÄ±:", err));
    }, [navigate]);



    return (
        <PanelTemplate
            title="My Account"
            rightContent={
                <div style={{ lineHeight: "2" }}>
                    <p><b>First Name: </b>{info.first_name}</p>
                    <p><b>Last Name: </b>{info.last_name}</p>
                    <p><b>Gender: </b>{info.gender}</p>
                    <p><b>Email: </b>{info.email}</p>

                    {/* ðŸ”¥ DÄ°KKAT: Backend'den 'adress' (tek 's') olarak geliyor olabilir */}
                    <p><b>Address: </b>{info.adress || info.address}</p>

                    <p><b>City : </b>{info.city}</p>
                    <p><b>Post Code: </b>{info.postcode}</p> {/* Backend'de 'postcode' demiÅŸtin */}
                    <p><b>Phone: </b>{info.phone || info.phone_number}</p>
                    <p><b>Age: </b>{info.age}</p>
                    <p><b>UserName: </b>{info.username}</p>

                    <hr />
                    <p><b>Customer Type: </b>{info.customer_type}</p>
                    <p><b>Total Purchased: </b>{info.purchased_quantity} adet</p>
                    <p><b>Credit Amount: </b>{info.credit_amount} â‚º</p>
                </div>
            }
        />
    );
}

export default Account;
