import React, { useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useState, useNavigate } from "react-router-dom";
import ProductAdd from "./ProductAdd";

function ProductList() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }
    const [products, setProducts] = useState([]);
    useEffect(() => {
        fetch("http://localhost:5000/product/list")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setProducts(data.products);
                }
            })
            .catch(err => console.error("Fetch error:", err));
    }, []);

    return (
        <PanelTemplate
            title="Product List"
            rightContent={
                <div>

                    <button type="button" onClick={() => git("productAdd")} style={{
                        backgroundColor: "#538456ff",
                        color: "white",
                        padding: "10px 20px",
                        borderRadius: "8px",
                        border: "none",
                        cursor: "pointer"
                    }}>ProductAdd</button>

                    <table border="1" style={{ width: "100%", marginTop: "20px", textAlign: "center" }}>
                        <thead>
                            <tr>
                                <th>Product ID</th>
                                <th>Category ID</th>
                                <th>Price</th>
                                <th>Material</th>
                                <th>Color</th>
                            </tr>
                        </thead>

                        <tbody>
                            {products.map(p => (
                                <tr key={p.product_id}>
                                    <td>{p.product_id}</td>
                                    <td>{p.category_id}</td>
                                    <td>{p.price}</td>
                                    <td>{p.material}</td>
                                    <td>{p.color}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            }
        />
    );
}

export default ProductList;
