import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function Orders() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }

    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/order/pending")
            .then(res => res.json())
            .then(data => {
                if (data.success)
                    setOrders(data.orders);
            });
    }, []);


    return (
        <PanelTemplate
            title="Orders"
            rightContent={
                <div>
                    {orders.length === 0 && (<p>No pending orders.</p>)}
                    {orders.map((order) => (
                        <div key={order.pending_order_id}
                            style={{
                                background: "white",
                                padding: "15px",
                                width: "100%",
                                marginBottom: "15px",
                                borderRadius: "40px",
                                boxShadow: "0 2px 8px rgba(0,0,0,0.1)"
                            }}>
                            <h4>{order.customer_name}</h4>
                            <p>Tutar: {order.total_amount} â‚º</p>
                            <p>Tarih: {new Date(order.created_at).toLocaleString()}</p>

                            <button
                                style={{
                                    backgroundColor: "#538456",
                                    color: "white",
                                    padding: "8px 16px",
                                    border: "none",
                                    borderRadius: "8px",
                                    cursor: "pointer"
                                }}
                            >
                                Detay
                            </button>
                            <button
                                style={{
                                    backgroundColor: "#538456",
                                    color: "white",
                                    padding: "8px 16px",
                                    border: "none",
                                    borderRadius: "8px",
                                    cursor: "pointer"
                                }}
                            >
                                Onay
                            </button>
                        </div>

                    ))}
                </div>
            }
        />
    );
}

export default Orders;
