import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";
import ProductAdd from "./ProductAdd";

function ProductList() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }
    const [products, setProducts] = React.useState([]);
    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                //ürünleri state kaydeder.
                if (data.success) {
                    setProducts(data.products);
                }
            });
    }, []);

    return (
        <PanelTemplate
            title="Product List"
            rightContent={
                <div>
                    <button type="button" onClick={() => git("productAdd")} style={{
                        backgroundColor: "#538456ff",
                        color: "white",
                        padding: "10px 20px",
                        borderRadius: "8px",
                        border: "none",
                        cursor: "pointer"
                    }}>ProductAdd</button>

                    <div className="product-grid">
                        {products.map(p => (
                            <div className="product-card" key={p.product_id}>
                                <img
                                    src="public/product-images/scarf1.png"
                                    alt="Product"
                                    className="product-image"
                                />
                                <h3>{p.material} {p.color}</h3>
                                <p>{p.price} ₺</p>

                                <button>Sepete Ekle</button>
                            </div>
                        ))}
                    </div>
                </div>

            }
        />
    );
}

export default ProductList;
