import { useEffect, useState } from "react";
import React from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function MyOrder() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }

    useEffect(() => {
        // Login dosyasında düzelttiğimiz 'info' paketini okuyoruz
        const storedInfo = localStorage.getItem("info");
        if (storedInfo) {
            const info = JSON.parse(storedInfo);
            const pId = info.person_id;

            fetch(`http://localhost:5003/order/my-orders/${pId}`)
                .then(res => res.json())
                .then(data => {
                    if (data.success) {
                        setOrders(data.orders);
                    }
                    setLoading(false);
                })
                .catch(err => {
                    console.error("Sipariş çekme hatası:", err);
                    setLoading(false);
                });
        }
    }, []);

    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const tableStyle = {
        padding: "12px",
        textAlign: "left",
        border: "1px solid #ddd"
    };

    return (
        <PanelTemplate
            title="My Order"
            rightContent={
                <div style={{ padding: "20px" }}>
                    {loading ? (
                        <p>Loading your orders...</p>
                    ) : orders.length === 0 ? (
                        <p>You don't have any orders yet.</p>
                    ) : (
                        <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "10px" }}>
                            <thead>
                                <tr style={{ backgroundColor: "#538456ff", color: "white" }}>
                                    <th style={tableStyle}>Order ID</th>
                                    <th style={tableStyle}>Date</th>
                                    <th style={tableStyle}>Total Amount</th>
                                    <th style={tableStyle}>Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {orders.map((o) => (
                                    <tr key={o.order_id} style={{ borderBottom: "1px solid #ddd" }}>
                                        <td style={tableStyle}>#{o.order_id}</td>
                                        <td style={tableStyle}>{o.order_date}</td>
                                        <td style={tableStyle}>{o.total_amount} ₺</td>
                                        <td style={tableStyle}>
                                            <span style={{ color: "green", fontWeight: "bold" }}>
                                                {o.status}
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            }
        />
    );
}

export default MyOrder;
