import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate, useLocation } from "react-router-dom";
import ProductAdd from "./ProductAdd";

function ProductList() {
    const navigate = useNavigate();
    const location = window.location;


    const role = location.pathname.startsWith("/manager") ? "manager" : location.pathname.startsWith("/employee") ? "employee" : "customer";

    // Rol’e göre temel path’i belirle
    const basePath = role === "manager" ? "/manager" : role === "employee" ? "/employee" : "/customer";

    const git = (page) => {
        navigate(`${basePath}/${page}`)
    }

    const [products, setProducts] = React.useState([]);
    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                //ürünleri state kaydeder.
                if (data.success) {
                    setProducts(data.products);
                }
            });
    }, []);

    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };

    return (
        <PanelTemplate
            title="Product List"
            rightContent={
                <div>
                    {role == "manager" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    {role == "employee" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    <div className="product-grid">
                        {products.map(p => (
                            <div className="product-card" key={p.product_id}>
                                {role == "manager" && (
                                    <>
                                        <img
                                            src={productImages[p.category_id] || "/images/default.png"}
                                            alt="Product"
                                            className="product-image"
                                        />

                                        <h3>{p.product_name}</h3>
                                        <p>{p.material}</p>
                                        <p>{p.price} ₺</p>

                                        <button>Sepete Ekle</button>
                                        <button>Delete</button>
                                    </>
                                )}
                                {role == "employee" && (
                                    <>
                                        <img
                                            src={productImages[p.category_id] || "/images/default.png"}
                                            alt="Product"
                                            className="product-image"
                                        />

                                        <h3>{p.product_name}</h3>
                                        <p>{p.material}</p>
                                        <p>{p.price} ₺</p>

                                        <button>Sepete Ekle</button>
                                        <button>Delete</button>
                                    </>
                                )}
                                {role == "customer" && (
                                    <>
                                        <img
                                            src={productImages[p.category_id] || "/images/default.png"}
                                            alt="Product"
                                            className="product-image"
                                        />

                                        <h3>{p.product_name}</h3>
                                        <p>{p.material}</p>
                                        <p>{p.price} ₺</p>

                                        <button>Sepete Ekle</button>
                                    </>
                                )}
                            </div>
                        ))}
                    </div>
                </div>

            }
        />
    );
}

export default ProductList;
