import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";

function Orders() {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/order/pending")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setOrders(data.orders);
                }
            });
    }, []);

    const approveOrder = async (pendingOrderId) => {
        // Fonksiyon çalıştığı anda güncel info'yu oku
        const currentInfo = JSON.parse(localStorage.getItem("info"));

        // Eğer info içindeki rol 'customer' ise bu bir hatadır, engelle!
        if (!currentInfo || currentInfo.role === "customer") {
            alert("Yetkisiz işlem! Lütfen personel hesabı ile tekrar giriş yapın.");
            return;
        }

        const approvedById = currentInfo.person_id;

        // Fetch isteğini gönder...
        const res = await fetch(`http://localhost:5003/order/approve/${pendingOrderId}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                approved_by_id: approvedById,
                approved_by_role: currentInfo.role
            })
        });
        // ...
    };

    return (
        <PanelTemplate
            title="Orders"
            rightContent={
                <div>
                    {orders.length === 0 && <p>No pending orders.</p>}

                    {orders.length > 0 && (
                        <table
                            border="1"
                            style={{
                                width: "100%",
                                marginTop: "20px",
                                textAlign: "center"
                            }}
                        >
                            <thead>
                                <tr>
                                    <th>Pending Order ID</th>
                                    <th>Customer</th>
                                    <th>Order Date</th>
                                    <th>Total Amount</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {orders.map(order => (
                                    <tr key={order.pending_order_id}>
                                        <td>{order.pending_order_id}</td>
                                        <td>{order.customer_name}</td>
                                        <td>{order.order_date}</td>
                                        <td>{order.total_amount} ₺</td>
                                        <td>
                                            <button
                                                onClick={() =>
                                                    approveOrder(order.pending_order_id)
                                                }
                                                style={{
                                                    backgroundColor: "#c50b0b",
                                                    color: "white",
                                                    padding: "8px 20px",
                                                    borderRadius: "8px",
                                                    border: "none",
                                                    cursor: "pointer"
                                                }}
                                            >
                                                Onayla
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            }
        />
    );
}

export default Orders;
