import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate, useLocation } from "react-router-dom";
import ProductAdd from "./ProductAdd";
import ProductCart from "./ProductCart";
import { preconnect } from "react-dom";

function ProductList() {
    const navigate = useNavigate();
    const location = window.location;

    const role = location.pathname.startsWith("/manager") ? "manager" : location.pathname.startsWith("/employee") ? "employee" : "customer";

    // Rol’e göre temel path’i belirle
    const basePath = role === "manager" ? "/manager" : role === "employee" ? "/employee" : "/customer";

    const git = (page) => {
        navigate(`${basePath}/productList/${page}`)
    }

    const [products, setProducts] = React.useState([]);
    const [cart, setCart] = useState([]);
    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                //ürünleri state kaydeder.
                if (data.success) {
                    setProducts(data.products);
                }
            });
    }, []);

    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Bu ürünü silmek istediğine emin misin?")) return;

        try {
            const response = await fetch(`http://localhost:5003/product/delete/${id}`, {
                method: "DELETE"
            });

            const data = await response.json();

            if (data.success) {
                alert("Product deleted!");

                // listeyi ekrandan da sil
                setProducts(prev => prev.filter(p => p.product_id !== id));
            } else {
                alert(data.message);
            }

        } catch (err) {
            console.error("Delete error:", err);
            alert("Error while deleting product.");
        }
    };

    const addToCart = (product) => {
        setCart(prevCart => {
            const existingItem = prevCart.find(item => item.product.product_id === product.product_id);

            if (existingItem) {
                if (existingItem.quantity >= product.quantity) {
                    alert("There is not enough stock for this product.");
                    return prevCart;
                }
                return prevCart.map(item =>
                    item.product.product_id === product.product_id
                        ? { ...item, quantity: item.quantity + 1 }
                        : item
                );
            }
            return [...prevCart, { product, quantity: 1 }];

        });
    };

    return (
        <PanelTemplate
            title="Product List"
            rightContent={
                <div>
                    {role == "manager" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    {role == "employee" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    {role == "customer" && (
                        <div style={{ display: "flex", justifyContent: "flex-end" }}>

                            <button
                                onClick={() => git("account")}
                                style={{
                                    backgroundColor: "#538456ff",
                                    color: "white",
                                    padding: "10px 20px",
                                    borderRadius: "8px",
                                    border: "none",
                                    cursor: "pointer"
                                }}
                            >
                                My Account
                            </button>

                            <button
                                onClick={() => git("productCart")}
                                style={{
                                    backgroundColor: "#538456ff",
                                    color: "white",
                                    padding: "10px 20px",
                                    borderRadius: "8px",
                                    border: "none",
                                    cursor: "pointer"
                                }}
                            >
                                Sepet
                            </button>

                        </div>

                    )}
                    <div className="product-grid">
                        {products.map(p => (
                            <div className="product-card" key={p.product_id}>
                                {role == "manager" && (
                                    <button type="button" onClick={() => git("ProductCart")} style={{
                                        backgroundColor: "transparent",
                                        color: "inherit",
                                        padding: "0px 0px",
                                        borderRadius: "10px",
                                        border: "none",
                                        cursor: "pointer",

                                    }}>
                                        <img
                                            src={productImages[p.category_id] || "/images/default.png"}
                                            alt="Product"
                                            className="product-image"
                                        />

                                        <h3>{p.product_name}</h3>
                                        <p>{p.material}</p>
                                        <p>{p.price} ₺</p>


                                        <button
                                            type="button"
                                            onClick={() => handleDelete(p.product_id)}
                                            style={{
                                                backgroundColor: "#cd2014ff",
                                                color: "white",
                                                padding: "10px 20px",
                                                borderRadius: "8px",
                                                border: "none",
                                                cursor: "pointer"

                                            }}>
                                            Delete
                                        </button>
                                    </button>
                                )}
                                {role == "employee" && (
                                    <>
                                        <button type="button" onClick={() => git("ProductCart")} style={{
                                            backgroundColor: "transparent",
                                            color: "inherit",
                                            padding: "0px 0px",
                                            borderRadius: "8px",
                                            border: "none",
                                            cursor: "pointer",

                                        }}>
                                            <img
                                                src={productImages[p.category_id] || "/images/default.png"}
                                                alt="Product"
                                                className="product-image"
                                            />

                                            <h3>{p.product_name}</h3>
                                            <p>{p.material}</p>
                                            <p>{p.price} ₺</p>
                                        </button>

                                        <button
                                            type="button"
                                            onClick={() => handleDelete(p.product_id)}
                                            style={{
                                                backgroundColor: "#cd2014ff",
                                                color: "white",
                                                padding: "10px 20px",
                                                borderRadius: "8px",
                                                border: "none",
                                                cursor: "pointer"

                                            }}>
                                            Delete
                                        </button>
                                    </>
                                )}
                                {role == "customer" && (
                                    <>
                                        <button type="button" onClick={() => git("ProductCart")} style={{
                                            backgroundColor: "transparent",
                                            color: "inherit",
                                            padding: "0px 0px",
                                            borderRadius: "8px",
                                            border: "none",
                                            cursor: "pointer",

                                        }}>
                                            <img
                                                src={productImages[p.category_id] || "/images/default.png"}
                                                alt="Product"
                                            />

                                            <h3>{p.product_name}</h3>
                                            <p>{p.material}</p>
                                            <p>{p.price} ₺</p>

                                            <button type="button" onClick={() => addToCart}>Sepete Ekle</button>
                                        </button>
                                    </>
                                )}
                            </div>
                        ))}
                    </div>
                </ div>

            }
        />
    );
}

export default ProductList;
