import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";

function ProductCart() {
    const [cart, setCart] = useState([]);
    const [info, setInfo] = useState(null);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setProducts(data.products);
                }
            });
    }, []);


    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };


    useEffect(() => {
        const storedInfo = localStorage.getItem("info");
        if (storedInfo) {
            setInfo(JSON.parse(storedInfo));
        }
    }, []);

    /* üîπ username & cartKey */
    const username = info?.username;
    const cartkey = username ? `cart_${username}` : null;


    useEffect(() => {
        if (!cartkey) return;

        const storedCart = JSON.parse(localStorage.getItem(cartkey)) || [];
        setCart(storedCart);
    }, [cartkey]); // üî• cartkey deƒüi≈üince MUTLAKA √ßalƒ±≈üƒ±r


    const decreaseQuantity = (productId) => {
        if (!cartkey) return;

        const updatedCart = cart
            .map(item =>
                item.product.product_id === productId
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            )
            .filter(item => item.quantity > 0);

        localStorage.setItem(cartkey, JSON.stringify(updatedCart));
        setCart(updatedCart);
    };

    const clearCart = () => {
        if (!cartkey) return;

        localStorage.removeItem(cartkey);
        setCart([]);
    };


    return (
        <PanelTemplate
            title="Product Cart"
            rightContent={
                <div>
                    {cart.length === 0 && <p>Sepet bo≈ü</p>}

                    {cart.map(item => (
                        <div key={item.product.product_id}>
                            <div className="product-card">
                                <h4>{item.product.product_name}</h4>

                                <img
                                    src={
                                        productImages[item.product.category_id]
                                        || "/images/default.png"
                                    }
                                    alt="Product"
                                    className="product-image"
                                />
                                <h3>{p.product_name}</h3>
                                <p>{p.material}</p>
                                <p>{p.price} ‚Ç∫</p>
                                <p>Adet: {item.quantity}</p>
                            </div>

                            <button
                                onClick={() =>
                                    decreaseQuantity(item.product.product_id)
                                }
                            >
                                Adet Azalt
                            </button>
                        </div>
                    ))}


                    {cart.length > 0 && (
                        <button onClick={clearCart}>
                            Sepeti Temizle
                        </button>
                    )}
                </div>
            }
        />
    );
}

export default ProductCart;
