import React, { useState } from "react";
import "../../styles/Selection.css";
import gorsel from "../../assets/image_3840.webp";
import { useNavigate } from "react-router-dom";

function CustomerLogin() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const login = () => {
    navigate('/login/customer');
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // 1. ADIM: Login isteği atıyoruz (Endpoint'i /customer/login olarak kontrol et)
      const response = await fetch("http://localhost:5003/customer/login", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (data.success) {
        // 2. ADIM: KRİTİK NOKTA! 
        // Backend'den dönen person_id'yi localStorage'a kaydediyoruz.
        // Böylece sepet (cart) sayfasında "Kimin siparişi bu?" dediğimizde buradan okuyacağız.
        localStorage.setItem("user_id", data.person_id);
        localStorage.setItem("role", "customer");
        localStorage.setItem("username", username); // İstersen ekranda hoş geldin Merve demek için tutabilirsin

        setMessage("Login Successful! Redirecting...");

        // 3. ADIM: Yönlendirme
        setTimeout(() => navigate("/customer/productList"), 1000);
      } else {
        setMessage(data.message || "Giriş başarısız.");
      }
    } catch (err) {
      console.error("Login error:", err);
      setMessage("Sunucuya bağlanılamadı.");
    }
  };

  return (
    <div className="roll-selection">
      <div className="roll-left">
        <h2> Customer Login </h2>
        <form className="login-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="UserName"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
          <button type="button" onClick={login}>
            Back
          </button>
        </form>
        {message && <p className="message">{message}</p>}
      </div>

      <div className="roll-right">
        <img src={gorsel} alt="Rol-Görseli" />
      </div>
    </div>
  );
}

export default CustomerLogin;
