import { useEffect, useState } from "react";
import React from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function MyOrder() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }

    useEffect(() => {
        const info = JSON.parse(localStorage.getItem("info"));
        const personId = info?.person_id;
        if (personId) {
            fetch(`http://localhost:5003/order/my-orders/${personId}`)
                .then(res => res.json())
                .then(data => {
                    if (data.success) {
                        setOrders(data.orders);
                    }
                    setLoading(false);
                })
                .catch(err => console.error("Hata:", err));
        }
    }, []);

    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);


    return (
        <PanelTemplate
            title="My Order"
            rightContent={
                <table
                    border="1"
                    style={{
                        width: "100%",
                        marginTop: "20px",
                        textAlign: "center"
                    }}
                >
                    <thead>
                        <tr>
                            <th>Pending Order ID</th>
                            <th>Customer</th>
                            <th>Order Date</th>
                            <th>Total Amount</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map(order => (
                            <tr key={order.pending_order_id}>
                                <td>{order.pending_order_id}</td>
                                <td>{order.customer_name}</td>
                                <td>{order.order_date}</td>
                                <td>{order.total_amount} â‚º</td>
                                <td>
                                    <button
                                        onClick={() =>
                                            approveOrder(order.pending_order_id)
                                        }
                                        style={{
                                            backgroundColor: "#c50b0b",
                                            color: "white",
                                            padding: "8px 20px",
                                            borderRadius: "8px",
                                            border: "none",
                                            cursor: "pointer"
                                        }}
                                    >
                                        Confirm
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            }
        />
    );
}

export default MyOrder;
