import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import SplashScreen from "./SplashScreen";

function Orders() {
    const [pendingOrders, setPendingOrders] = useState([]);
    const [approvedOrders, setApprovedOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    // Verileri her iki tablodan da çeken fonksiyon
    const fetchData = async () => {
        setLoading(true);
        try {
            // 1. Bekleyen siparişleri çek (pending_orders tablosu)
            const resP = await fetch("http://localhost:5003/order/pending");
            const dataP = await resP.json();
            if (dataP.success) setPendingOrders(dataP.orders);

            // 2. Onaylanmış siparişleri çek (orders tablosu)
            const resA = await fetch("http://localhost:5003/order/approved-list");
            const dataA = await resA.json();
            if (dataA.success) setApprovedOrders(dataA.orders);
        } catch (error) {
            console.error("Veri çekme hatası:", error);
        } finally {
            setTimeout(() => setLoading(false), 800);
        }
    };

    useEffect(() => { fetchData(); }, []);

    // ✅ ONAYLA: Veriyi kalıcı tabloya taşır ve arayüzü günceller
    const handleApprove = async (id) => {
        const res = await fetch(`http://localhost:5003/order/approve/${id}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                approved_by_id: localStorage.getItem("person_id"),
                approved_by_role: localStorage.getItem("role")
            })
        });
        const data = await res.json();
        if (data.success) {
            alert("Sipariş Onaylandı ve Arşive Taşındı!");
            fetchData(); // Listeleri yenilemek için verileri tekrar çek
        }
    };

    // ❌ REDDET/SİL: Veriyi veritabanından tamamen yok eder
    const handleReject = async (id) => {
        if (!window.confirm("Bu siparişi reddetmek ve tamamen silmek istediğine emin misin?")) return;

        const res = await fetch(`http://localhost:5003/order/reject/${id}`, {
            method: "DELETE"
        });
        const data = await res.json();
        if (data.success) {
            alert("Sipariş tamamen silindi.");
            setPendingOrders(prev => prev.filter(o => o.pending_order_id !== id));
        }
    };

    if (loading) return <SplashScreen text="Siparişler Hazırlanıyor..." />;

    return (
        <PanelTemplate
            title="Order Management System"
            rightContent={
                <div style={{ padding: "20px" }}>

                    {/* --- ÜST TABLO: ONAYLANAN SİPARİŞLER (ARCHIVE) --- */}
                    <h3 style={{ color: "#27ae60", borderBottom: "2px solid #27ae60", paddingBottom: "10px" }}>
                        ✅ Approved Orders (History)
                    </h3>
                    {approvedOrders.length === 0 ? <p>Henüz onaylanmış sipariş yok.</p> : (
                        <table border="1" style={tableStyle}>
                            <thead style={{ backgroundColor: "#eafaf1" }}>
                                <tr>
                                    <th>Order ID</th>
                                    <th>Customer</th>
                                    <th>Total Amount</th>
                                    <th>Approved By</th>
                                    <th>Order Date</th>
                                </tr>
                            </thead>
                            <tbody>
                                {approvedOrders.map(o => (
                                    <tr key={o.order_id}>
                                        <td>#{o.order_id}</td>
                                        <td>{o.customer_name}</td>
                                        <td>{o.total_amount} ₺</td>
                                        <td><small>{o.approved_by_name} ({o.approved_by_role})</small></td>
                                        <td>{o.order_date}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}

                    <div style={{ margin: "60px 0" }}></div> {/* İki tablo arası boşluk */}

                    {/* --- ALT TABLO: ONAY BEKLEYENLER (PENDING) --- */}
                    <h3 style={{ color: "#e67e22", borderBottom: "2px solid #e67e22", paddingBottom: "10px" }}>
                        ⏳ Pending Orders (Action Required)
                    </h3>
                    {pendingOrders.length === 0 ? <p>Onay bekleyen sipariş bulunmuyor.</p> : (
                        <table border="1" style={tableStyle}>
                            <thead style={{ backgroundColor: "#fef5e7" }}>
                                <tr>
                                    <th>ID</th>
                                    <th>Customer</th>
                                    <th>Total Amount</th>
                                    <th>Request Date</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {pendingOrders.map(o => (
                                    <tr key={o.pending_order_id}>
                                        <td>{o.pending_order_id}</td>
                                        <td>{o.customer_name}</td>
                                        <td>{o.total_amount} ₺</td>
                                        <td>{o.order_date}</td>
                                        <td>
                                            <button onClick={() => handleApprove(o.pending_order_id)} style={confirmBtn}>Approve</button>
                                            <button onClick={() => handleReject(o.pending_order_id)} style={rejectBtn}>Reject & Delete</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            }
        />
    );
}


export default Orders;
