import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { jsPDF } from "jspdf";
import "jspdf-autotable";

function Reports() {
    const [reportData, setReportData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [personDetails, setPersonDetails] = useState([]);

    // 1. Verileri Çekme (Fetch)
    useEffect(() => {
        fetch("http://localhost:5000/manager/generalReport")
            .then((res) => res.json())
            .then((data) => {
                if (data.success) {
                    setReportData(data.report); // Backend'deki 'report' anahtarı ile eşleşiyor
                }
                setLoading(false);
            })
            .catch((err) => {
                console.error("Veri çekme hatası:", err);
                setLoading(false);
            });
    }, []);

    // 2. Satıra Tıklayınca Detay Getirme (Detail)
    const handlePersonClick = (personId) => {
        setSelectedPerson(personId);
        fetch(`http://localhost:5000/manager/person-orders/${personId}`)
            .then((res) => res.json())
            .then((data) => {
                if (data.success) {
                    setPersonDetails(data.orders);
                }
            });
    };

    // 3. PDF İndirme Fonksiyonu
    const downloadPDF = () => {
        const doc = new jsPDF();
        doc.text("Genel Personel ve Musteri Listesi", 14, 15);
        const rows = reportData.map(p => [p.person_id, p.first_name, p.last_name, p.role, p.email, p.city]);
        doc.autoTable({
            head: [['ID', 'Ad', 'Soyad', 'Rol', 'Email', 'Sehir']],
            body: rows,
            startY: 20
        });
        doc.save("Genel_Rapor.pdf");
    };

    return (
        <PanelTemplate
            title="Reports"
            rightContent={
                <div className="panel-right">
                    <div style={{ display: 'flex', justifyContent: 'space-between', width: '100%', marginBottom: '20px', alignItems: 'center' }}>
                        <h2 style={{ color: "#2f4036" }}>Person Tablosu (Genel Rapor)</h2>
                        <button onClick={downloadPDF} className="panel_button" style={{ width: 'auto', padding: '10px 20px' }}>
                            PDF Olarak İndir
                        </button>
                    </div>

                    {/* ANA TABLO (MASTER) - EmployeeList stilinde */}
                    <table className="orders-table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Ad</th>
                                <th>Soyad</th>
                                <th>Rol</th>
                                <th>Email</th>
                                <th>Şehir</th>
                            </tr>
                        </thead>
                        <tbody>
                            {loading ? (
                                <tr><td colSpan="6">Yükleniyor...</td></tr>
                            ) : reportData.length > 0 ? (
                                reportData.map((person) => (
                                    <tr
                                        key={person.person_id}
                                        onClick={() => handlePersonClick(person.person_id)}
                                        style={{
                                            backgroundColor: selectedPerson === person.person_id ? '#d3e9dd' : 'white',
                                            cursor: 'pointer'
                                        }}
                                    >
                                        <td>{person.person_id}</td>
                                        <td>{person.first_name}</td>
                                        <td>{person.last_name}</td>
                                        <td>{person.role}</td>
                                        <td>{person.email}</td>
                                        <td>{person.city}</td>
                                    </tr>
                                ))
                            ) : (
                                <tr><td colSpan="6">Veritabanında kayıtlı kimse bulunamadı.</td></tr>
                            )}
                        </tbody>
                    </table>

                    {/* AYRINTI TABLOSU (DETAIL) - Hocanın istediği form */}
                    {selectedPerson && (
                        <div style={{ marginTop: '30px', width: '100%', padding: '20px', backgroundColor: 'white', borderRadius: '15px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
                            <h3 style={{ marginBottom: '15px', color: '#2f4036' }}>Kişiye Özel İşlem Geçmişi</h3>
                            <table className="orders-table">
                                <thead style={{ backgroundColor: '#7fa997' }}>
                                    <tr>
                                        <th>Sipariş ID</th>
                                        <th>Tarih</th>
                                        <th>Tutar</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {personDetails.length > 0 ? (
                                        personDetails.map((order) => (
                                            <tr key={order.order_id}>
                                                <td>{order.order_id}</td>
                                                <td>{order.order_date}</td>
                                                <td>{order.total_amount} TL</td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr><td colSpan="3">Bu kişiye ait henüz bir sipariş veya onay kaydı bulunmuyor.</td></tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            }
        />
    );
}

export default Reports;
