import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // URL'den ID'yi almak için
import PanelTemplate from "./PanelTemplate";

function Details() {
    const { id } = useParams(); // URL'deki productid'yi yakalar
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);

    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };


    // Details.js içinde
    useEffect(() => {
        fetch(`http://localhost:5003/product/${id}`) // Portu 5003 yaptık
            .then((res) => {
                if (!res.ok) throw new Error("Sunucu hatası: " + res.status);
                return res.json();
            })
            .then((data) => {
                console.log("Gelen Veri:", data);
                setProduct(data);
            })
            .catch((err) => {
                console.error("Veri çekme hatası:", err);
            });
    }, [id]);
    const cartkey = personId ? `cart_${personId}` : null;


    useEffect(() => {
        if (!cartkey) {
            setCart([]);
            return;
        }

        const storedCart = JSON.parse(localStorage.getItem(cartkey)) || [];
        setCart(storedCart);
    }, [cartkey]);

    const addToCart = (product) => {
        if (!cartkey) {
            alert("User information could not be found, please log in again..");
            return;
        }
        let updatedCart = [...cart];
        const index = updatedCart.findIndex(item => item.product.product_id === product.product_id);
        if (index !== -1) {
            if (updatedCart[index].quantity >= product.quantity) { alert("Insufficient products in stock.!"); return; }
            updatedCart[index].quantity += 1;
        } else {
            if (product.quantity <= 0) { alert("This product is out of stock.!"); return; }
            updatedCart.push({ product, quantity: 1 });
        }
        localStorage.setItem(cartkey, JSON.stringify(updatedCart));
        setCart(updatedCart);
    };
    if (!product) return <div>Product not found.</div>;

    return (
        <PanelTemplate
            role="manager"
            rightContent={

                <div style={{ padding: "20px" }}>
                    <h2 style={{ borderBottom: "2px solid #eee", paddingBottom: "10px" }}>
                        Ürün Detay Paneli (Master-Detail)
                    </h2>

                    <div style={{ display: "flex", gap: "40px", marginTop: "20px", flexWrap: "wrap" }}>

                        {/* SOL TARAF: Ürün Görseli */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <img
                                src={product.image_url && product.image_url !== "/images/default.png"
                                    ? product.image_url
                                    : (productImages[Number(product.category_id)] || "/images/default.png")}
                                alt={`${product.material} ${product.category_name}`}
                                style={{
                                    width: "100%",           // Genişlik bulunduğu kabı doldursun
                                    height: "450px",         // ✅ SABİT BOYUT: Tüm resimler tam olarak bu yükseklikte olur
                                    objectFit: "cover",      // ✅ KRİTİK: Resmi yamultmadan alanı dolduracak şekilde kırpar
                                    borderRadius: "15px",    // Köşeleri yumuşatır
                                    boxShadow: "0 4px 15px rgba(0,0,0,0.1)", // Şık bir gölge ekler
                                    display: "block"
                                }}
                                onError={(e) => { e.target.src = "/images/default.png"; }}
                            />
                        </div>

                        {/* SAĞ TARAF: Veritabanı Detayları */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <div style={{ backgroundColor: "#f9f9f9", padding: "20px", borderRadius: "12px" }}>
                                <p><strong>Product ID:</strong> #{product.product_id}</p>
                                <p><strong>Catagory:</strong> {product.category_name}</p>
                                <p><strong>Material:</strong> {product.material}</p>
                                <p><strong>Color:</strong> {product.color}</p>
                                <p><strong>Price:</strong> <span style={{ color: "#2e7d32", fontSize: "1.2em", fontWeight: "bold" }}>{product.price} TL</span></p>
                                <p><strong>Stock Status:</strong> {product.quantity} Quantity</p>

                                <hr />
                                <p style={{ fontSize: "0.9em", color: "#666" }}>
                                    * Bu veriler <strong>view_product_details</strong> görünümü üzerinden getirilmiştir.
                                </p>
                            </div>

                            {/* EN ALT: Sepete Ekle Butonu */}
                            <button style={{
                                marginTop: "20px",
                                width: "100%",
                                padding: "15px",
                                backgroundColor: "#ff9800",
                                color: "white",
                                border: "none",
                                borderRadius: "8px",
                                cursor: "pointer",
                                fontSize: "1.1em",
                                fontWeight: "bold"
                            }}>
                                Add to Cart
                            </button>
                        </div>
                    </div>
                </div>
            }
        />
    );
}

export default Details;
