import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";

function Orders() {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/order/pending")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setOrders(data.orders);
                }
            });
    }, []);

    const approveOrder = async (pendingOrderId) => {
        const adminInfo = JSON.parse(localStorage.getItem("info"));

        // ðŸ”¥ BurasÄ± kritik: adminInfo.person_id o anki paneli kullanan kiÅŸidir.
        const adminId = adminInfo?.person_id;
        const adminRole = adminInfo?.role || "manager";

        if (!adminId) {
            alert("Oturum bilgisi bulunamadÄ±!");
            return;
        }

        try {
            const response = await fetch(`http://localhost:5003/order/approve/${pendingOrderId}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    approved_by_id: adminId, // DoÄŸru admin ID gÃ¶nderiliyor
                    approved_by_role: adminRole
                })
            });

            const data = await response.json();
            if (data.success) {
                alert("SipariÅŸ baÅŸarÄ±yla onaylandÄ±.");
                // Listeyi yenileme kodun...
            }
        } catch (err) {
            console.error("Onay hatasÄ±:", err);
        }
    };

    return (
        <PanelTemplate
            title="Orders"
            rightContent={
                <div>
                    {orders.length === 0 && <p>No pending orders.</p>}

                    {orders.length > 0 && (
                        <table
                            border="1"
                            style={{
                                width: "100%",
                                marginTop: "20px",
                                textAlign: "center"
                            }}
                        >
                            <thead>
                                <tr>
                                    <th>Pending Order ID</th>
                                    <th>Customer</th>
                                    <th>Order Date</th>
                                    <th>Total Amount</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {orders.map(order => (
                                    <tr key={order.pending_order_id}>
                                        <td>{order.pending_order_id}</td>
                                        <td>{order.customer_name}</td>
                                        <td>{order.order_date}</td>
                                        <td>{order.total_amount} â‚º</td>
                                        <td>
                                            <button
                                                onClick={() =>
                                                    approveOrder(order.pending_order_id)
                                                }
                                                style={{
                                                    backgroundColor: "#c50b0b",
                                                    color: "white",
                                                    padding: "8px 20px",
                                                    borderRadius: "8px",
                                                    border: "none",
                                                    cursor: "pointer"
                                                }}
                                            >
                                                Onayla
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            }
        />
    );
}

export default Orders;
