import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate, useLocation } from "react-router-dom";

function ProductList() {
    const navigate = useNavigate();
    const location = window.location;

    const role = location.pathname.startsWith("/manager") ? "manager" : location.pathname.startsWith("/employee") ? "employee" : "customer";

    const basePath = role === "manager" ? "/manager" : role === "employee" ? "/employee" : "/customer";

    const git = (page) => {
        navigate(`${basePath}/productList/${page}`)
    }

    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setProducts(data.products);
                }
            });
    }, []);


    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };

    const [info, setInfo] = useState(() => {
        const stored = localStorage.getItem("info");
        return stored ? JSON.parse(stored) : {};
    });

    const handleDelete = async (id) => {
        if (!window.confirm("Bu √ºr√ºn√º silmek istediƒüine emin misin?")) return;

        try {
            const response = await fetch(
                `http://localhost:5003/product/delete/${id}`,
                { method: "DELETE" }
            );

            const data = await response.json();

            if (data.success) {
                alert("Product deleted!");
                setProducts(prev =>
                    prev.filter(p => p.product_id !== id)
                );
            } else {
                alert(data.message);
            }
        } catch (err) {
            console.error("Delete error:", err);
            alert("Error while deleting product.");
        }
    };


    const [cart, setCart] = useState([]);

    /* üîπ username & cart key */
    // user_id login anƒ±nda kesinle≈ütiƒüi i√ßin en g√ºvenli anahtar budur
    const userId = localStorage.getItem("user_id");
    const cartkey = userId ? `cart_user_${userId}` : null;

    /* üîπ Sayfa a√ßƒ±lƒ±nca sepeti y√ºkle */
    useEffect(() => {
        if (cartkey) {
            const storedCart = JSON.parse(localStorage.getItem(cartkey)) || [];
            setCart(storedCart);
        } else {
            setCart([]);
        }
    }, [cartkey]);

    /* üîπ Sepete ekle (D√ºzeltilmi≈ü) */
    const addToCart = (product) => {
        // userId yoksa login'e y√∂nlendir veya uyar
        if (!userId) {
            alert("L√ºtfen √∂nce giri≈ü yapƒ±n!");
            return;
        }

        let updatedCart = [...cart];
        const index = updatedCart.findIndex(
            item => item.product.product_id === product.product_id
        );

        if (index !== -1) {
            // Stok kontrol√º
            if (updatedCart[index].quantity >= product.quantity) {
                alert("Stokta daha fazla √ºr√ºn yok!");
                return;
            }
            updatedCart[index].quantity += 1;
        } else {
            if (product.quantity <= 0) {
                alert("Bu √ºr√ºn stokta yok!");
                return;
            }
            updatedCart.push({ product, quantity: 1 });
        }

        setCart(updatedCart);
        localStorage.setItem(cartkey, JSON.stringify(updatedCart));
    };
    /* üîπ Sepet sayacƒ± */
    const totalCount = cart.reduce(
        (sum, item) => sum + item.quantity,
        0
    );





    return (
        <PanelTemplate
            title="Product List"
            rightContent={
                <div>
                    {role == "manager" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    {role == "employee" && (
                        <>
                            <button type="button" onClick={() => git("productAdd")} style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}>ProductAdd</button>
                        </>
                    )}
                    {role == "customer" && (
                        <div style={{ display: "flex", justifyContent: "flex-end" }}>

                            <button
                                onClick={() => git("account")}
                                style={{
                                    backgroundColor: "#538456ff",
                                    color: "white",
                                    padding: "10px 20px",
                                    borderRadius: "8px",
                                    border: "none",
                                    cursor: "pointer"
                                }}
                            >
                                {info?.username}
                            </button>

                            <button
                                onClick={() => git("productCart")}
                                style={{
                                    backgroundColor: "#538456ff",
                                    color: "white",
                                    padding: "10px 20px",
                                    borderRadius: "8px",
                                    border: "none",
                                    cursor: "pointer"
                                }}
                            >
                                Cart ({totalCount})
                            </button>

                        </div>

                    )}
                    <div className="product-grid">
                        {products.map(p => (
                            <div className="product-card" key={p.product_id}>
                                {role == "manager" && (
                                    <button type="button" onClick={() => git("ProductCart")} style={{
                                        backgroundColor: "transparent",
                                        color: "inherit",
                                        padding: "0px 0px",
                                        borderRadius: "10px",
                                        border: "none",
                                        cursor: "pointer",

                                    }}>
                                        <img
                                            src={productImages[p.category_id] || "/images/default.png"}
                                            alt="Product"
                                            className="product-image"
                                        />

                                        <h3>{p.product_name}</h3>
                                        <p>{p.material}</p>
                                        <p>{p.price} ‚Ç∫</p>


                                        <button
                                            type="button"
                                            onClick={() => handleDelete(p.product_id)}
                                            style={{
                                                backgroundColor: "#cd2014ff",
                                                color: "white",
                                                padding: "10px 20px",
                                                borderRadius: "8px",
                                                border: "none",
                                                cursor: "pointer"

                                            }}>
                                            Delete
                                        </button>
                                    </button>
                                )}
                                {role == "employee" && (
                                    <>
                                        <button type="button" onClick={() => git("ProductCart")} style={{
                                            backgroundColor: "transparent",
                                            color: "inherit",
                                            padding: "0px 0px",
                                            borderRadius: "8px",
                                            border: "none",
                                            cursor: "pointer",

                                        }}>
                                            <img
                                                src={productImages[p.category_id] || "/images/default.png"}
                                                alt="Product"
                                                className="product-image"
                                            />

                                            <h3>{p.product_name}</h3>
                                            <p>{p.material}</p>
                                            <p>{p.price} ‚Ç∫</p>
                                        </button>

                                        <button
                                            type="button"
                                            onClick={() => handleDelete(p.product_id)}
                                            style={{
                                                backgroundColor: "#cd2014ff",
                                                color: "white",
                                                padding: "10px 20px",
                                                borderRadius: "8px",
                                                border: "none",
                                                cursor: "pointer"

                                            }}>
                                            Delete
                                        </button>
                                    </>
                                )}
                                {role == "customer" && (
                                    <>
                                        <button type="button" onClick={() => git("ProductCart")} style={{
                                            backgroundColor: "transparent",
                                            color: "inherit",
                                            padding: "0px 0px",
                                            borderRadius: "8px",
                                            border: "none",
                                            cursor: "pointer",

                                        }}>
                                            <img
                                                src={productImages[p.category_id] || "/images/default.png"}
                                                alt="Product"
                                            />

                                            <h3>{p.product_name}</h3>
                                            <p>{p.material}</p>
                                            <p>{p.price} ‚Ç∫</p>

                                        </button>
                                        <button type="button" onClick={() => addToCart(p)}>Add to Cart</button>
                                    </>
                                )}
                            </div>
                        ))}
                    </div>
                </ div>

            }
        />
    );
}

export default ProductList;
