import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";

function Reports() {
    const [reportData, setReportData] = useState([]);

    useEffect(() => {
        // Tam URL kullanarak fetch yapıyoruz
        fetch("http://localhost:5000/manager/generalReport")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setReportData(data.report);
                }
            })
            .catch(err => console.error("Bağlantı Hatası:", err));
    }, []);

    return (
        <PanelTemplate
            title="Manager Reports"
            rightContent={
                <div className="panel-right" style={{ width: '100%', overflowX: 'auto' }}>
                    <h2 style={{ marginBottom: '20px', color: '#2f4036' }}>Person Tablosu Verileri</h2>

                    <table className="orders-table" style={{ fontSize: '12px' }}>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Ad</th>
                                <th>Soyad</th>
                                <th>Cinsiyet</th>
                                <th>Email</th>
                                <th>Adres</th>
                                <th>Şehir</th>
                                <th>Rol</th>
                            </tr>
                        </thead>
                        <tbody>
                            {reportData.length > 0 ? (
                                reportData.map((p) => (
                                    <tr key={p.person_id}>
                                        <td>{p.person_id}</td>
                                        <td>{p.first_name}</td>
                                        <td>{p.last_name}</td>
                                        <td>{p.gender}</td>
                                        <td>{p.email}</td>
                                        <td>{p.address}</td>
                                        <td>{p.city}</td>
                                        <td style={{ fontWeight: 'bold' }}>{p.rol}</td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="8" style={{ padding: '20px' }}>
                                        Veri tabanından veriler yüklenemedi. Lütfen Flask terminalini kontrol edin.
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            }
        />
    );
}

export default Reports;
