import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { jsPDF } from "jspdf";
import "jspdf-autotable";

function Reports() {
    const [persons, setPersons] = useState([]); // EmployeeList'teki employees gibi
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [personDetails, setPersonDetails] = useState([]);

    // 1. Person tablosundaki herkesi çek
    useEffect(() => {
        // Backend portun 5003 ise ona göre güncelleyebilirsin
        fetch("http://localhost:5003/manager/generalReport")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setPersons(data.report); // Backend'deki 'report' listesini persons state'ine atar
                }
            })
            .catch(err => console.error("Fetch error:", err));
    }, []);

    // 2. Ana-Ayrıntı: Satıra tıklayınca detayları getir
    const handlePersonClick = (id) => {
        setSelectedPerson(id);
        fetch(`http://localhost:5003/manager/person-orders/${id}`)
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setPersonDetails(data.orders);
                }
            });
    };

    // 3. PDF İndirme Fonksiyonu
    const downloadPDF = () => {
        const doc = new jsPDF();
        doc.text("Person Tablosu Genel Raporu", 14, 15);
        const rows = persons.map(p => [
            p.person_id, p.first_name, p.last_name, p.role, p.email, p.city
        ]);
        doc.autoTable({
            head: [['ID', 'First Name', 'Last Name', 'Role', 'Email', 'City']],
            body: rows,
            startY: 20
        });
        doc.save("Person_General_Report.pdf");
    };

    return (
        <PanelTemplate
            title="Person Reports"
            rightContent={
                <div style={{ width: "100%" }}>
                    <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
                        <h2 style={{ color: "#2f4036" }}>General Person Report</h2>
                        <button
                            type="button"
                            onClick={downloadPDF}
                            style={{
                                backgroundColor: "#538456ff",
                                color: "white",
                                padding: "10px 20px",
                                borderRadius: "8px",
                                border: "none",
                                cursor: "pointer"
                            }}
                        >
                            PDF Download
                        </button>
                    </div>

                    {/* ANA TABLO (MASTER) - EmployeeList ile aynı yapı */}
                    <table className="orders-table" border="1">
                        <thead>
                            <tr>
                                <th>Person ID</th>
                                <th>First Name</th>
                                <th>Last Name</th>
                                <th>Gender</th>
                                <th>Email</th>
                                <th>Address</th>
                                <th>City</th>
                                <th>Postal Code</th>
                                <th>Phone Number</th>
                                <th>Age</th>
                                <th>Username</th>
                                <th>Role</th>
                            </tr>
                        </thead>

                        <tbody>
                            {persons.map(p => (
                                <tr
                                    key={p.person_id}
                                    onClick={() => handlePersonClick(p.person_id)}
                                    style={{
                                        cursor: "pointer",
                                        backgroundColor: selectedPerson === p.person_id ? "#d3e9dd" : "white"
                                    }}
                                >
                                    <td>{p.person_id}</td>
                                    <td>{p.first_name}</td>
                                    <td>{p.last_name}</td>
                                    <td>{p.gender}</td>
                                    <td>{p.email}</td>
                                    <td>{p.address}</td>
                                    <td>{p.city}</td>
                                    <td>{p.postal_code}</td>
                                    <td>{p.phone_number}</td>
                                    <td>{p.age}</td>
                                    <td>{p.username}</td>
                                    <td style={{ fontWeight: "bold" }}>{p.role}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* AYRINTI TABLOSU (DETAIL) - Hocanın istediği form */}
                    {selectedPerson && (
                        <div style={{ marginTop: "40px", padding: "20px", background: "white", borderRadius: "12px", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>
                            <h3 style={{ color: "#2f4036", marginBottom: "15px" }}>İşlem Detayları (ID: {selectedPerson})</h3>
                            <table className="orders-table">
                                <thead style={{ backgroundColor: "#7fa997" }}>
                                    <tr>
                                        <th>Order ID</th>
                                        <th>Date</th>
                                        <th>Total Amount</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {personDetails.length > 0 ? (
                                        personDetails.map(order => (
                                            <tr key={order.order_id}>
                                                <td>{order.order_id}</td>
                                                <td>{order.order_date}</td>
                                                <td>{order.total_amount} TL</td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr><td colSpan="3" style={{ textAlign: "center", padding: "10px" }}>No orders found for this person.</td></tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            }
        />
    );
}

export default Reports;
