import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { jsPDF } from "jspdf";
import "jspdf-autotable";

function Reports() {
    const [reportData, setReportData] = useState([]);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [personDetails, setPersonDetails] = useState([]);

    // Sayfa açıldığında verileri çek
    useEffect(() => {
        fetch("http://localhost:5000/manager/generalReport")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setReportData(data.report);
                }
            })
            .catch(err => console.error("Veri çekme hatası:", err));
    }, []);

    const handlePersonClick = (personId) => {
        setSelectedPerson(personId);
        fetch(`http://localhost:5000/manager/person-orders/${personId}`)
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setPersonDetails(data.orders);
                }
            });
    };

    const downloadPDF = () => {
        const doc = new jsPDF();
        doc.text("Genel Isletme Raporu (Person Listesi)", 14, 15);
        const rows = reportData.map(p => [p.ad_soyad, p.rol, p.islem_sayisi, `${p.toplam_ciro} TL`]);
        doc.autoTable({
            head: [['Ad Soyad', 'Rol', 'Islem Sayisi', 'Toplam Ciro']],
            body: rows,
            startY: 20
        });
        doc.save("person_raporu.pdf");
    };

    return (
        <PanelTemplate
            title="Reports"
            rightContent={
                <div className="panel-right">
                    <div style={{ display: 'flex', justifyContent: 'space-between', width: '100%', marginBottom: '20px', alignItems: 'center' }}>
                        <h1 style={{ fontSize: '24px', fontWeight: 'bold' }}>Personel ve Müşteri Raporu</h1>
                        <button onClick={downloadPDF} className="panel_button" style={{ width: 'auto', padding: '10px 20px' }}>
                            PDF Olarak İndir
                        </button>
                    </div>

                    {/* ANA TABLO (MASTER) */}
                    <table className="orders-table">
                        <thead>
                            <tr>
                                <th>Ad Soyad</th>
                                <th>Rol</th>
                                <th>İşlem Sayısı</th>
                                <th>Toplam Ciro</th>
                            </tr>
                        </thead>
                        <tbody>
                            {reportData.length > 0 ? (
                                reportData.map((person) => (
                                    <tr
                                        key={person.person_id}
                                        onClick={() => handlePersonClick(person.person_id)}
                                        style={{
                                            backgroundColor: selectedPerson === person.person_id ? '#d3e9dd' : 'white',
                                            cursor: 'pointer'
                                        }}
                                    >
                                        <td>{person.ad_soyad}</td>
                                        <td>{person.rol}</td>
                                        <td>{person.islem_sayisi}</td>
                                        <td>{person.toplam_ciro.toLocaleString()} TL</td>
                                    </tr>
                                ))
                            ) : (
                                <tr><td colSpan="4">Veri yükleniyor veya Person tablosu boş...</td></tr>
                            )}
                        </tbody>
                    </table>

                    {/* AYRINTI TABLOSU (DETAIL) */}
                    {selectedPerson && (
                        <div style={{ marginTop: '30px', width: '100%' }}>
                            <h2 style={{ marginBottom: '10px' }}>İşlem Detayları</h2>
                            <table className="orders-table">
                                <thead>
                                    <tr>
                                        <th>Sipariş ID</th>
                                        <th>Tarih</th>
                                        <th>Tutar</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {personDetails.map((order) => (
                                        <tr key={order.order_id}>
                                            <td>{order.order_id}</td>
                                            <td>{order.order_date}</td>
                                            <td>{order.total_amount} TL</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            }
        />
    );
}

export default Reports;
