import React, { useEffect } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function Account() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }
    const [info, setInfo] = React.useState({});

    // Bilgilerin çekildiği sayfadaki useEffect (Örnek)
    useEffect(() => {

        const personId = localStorage.getItem("person_id");
        if (!userId || userId === "null") {
            console.error("ID bulunamadı, login sayfasına yönlendiriliyor...");
            navigate("/login");
            return;
        }



        fetch(`http://localhost:5003/customer/info/${personId}`)

            .then(res => res.json())
            .then(data => {
                if (data.success) { setInfo(data.info); }
            });
    }, []);
    if (!info) return <p>Loading...</p>;

    return (
        <PanelTemplate
            title="My Account"
            rightContent={
                <div>
                    <p><b>First Name: </b>{info.first_name}</p>
                    <p><b>Last Name: </b>{info.last_name}</p>
                    <p><b>Email: </b>{info.email}</p>
                    <p><b>Phone: </b>{info.phone_number}</p>
                    <p><b>UserName: </b>{info.username}</p>
                    <p><b>Address: </b>{info.address}</p>
                </div>


            }
        />
    );
}

export default Account;
