import React, { useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function ProductAdd() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }

    const [formData, setFormData] = useState({
        category_id: "",
        product_type: "",
        price: "",
        material: "",
        color: "",
        size: "",       // Scarf
        length: "",     // Shawl
        stretch: ""     // Bonnet
    });

    const handleChange = (e) => {
        setFormData({
            formData, [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const response = await fetch("http://localhost:5003/product/insert", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(formData),
        });

        const data = await response.json();
        alert(data.message);
    };

    return (
        <PanelTemplate
            title="Product Add"
            rightContent={
                <div>
                    <form onSubmit={handleSubmit} style={{ width: "400px" }}>
                        <label>Catagory</label>
                        <select name="catagory_id" onChange={handleChange}>
                            <options value="">Select Category</options>
                            <option value="1">Scarf</option>
                            <option value="2">Shawl</option>
                            <option value="3">Bonnet</option>
                        </select>

                        <label>Product Type</label>
                        <select name="product_type" onChange={handleChange}>
                            <option value="">Select Type</option>
                            <option value="Scarf">Scarf</option>
                            <option value="Shawl">Shawl</option>
                            <option value="Bonnet">Bonnet</option>
                        </select>

                        <input
                            name="price"
                            type="number"
                            placeholder="Price"
                            onChange={handleChange}
                        />
                        <input
                            name="material"
                            type="text"
                            placeholder="Material"
                            onChange={handleChange}
                        />
                        <input
                            name="color"
                            type="text"
                            placeholder="Color"
                            onChange={handleChange}
                        />

                        {/* Dynamic Fields */}
                        {formData.product_type === "Scarf" && (
                            <input
                                name="size"
                                type="text"
                                placeholder="Size (e.g., 90x90)"
                                onChange={handleChange}
                            />
                        )}

                        {formData.product_type === "Shawl" && (
                            <input
                                name="length"
                                type="number"
                                placeholder="Length (cm)"
                                onChange={handleChange}
                            />
                        )}

                        {formData.product_type === "Bonnet" && (
                            <input
                                name="stretch"
                                type="text"
                                placeholder="Stretch Type"
                                onChange={handleChange}
                            />
                        )}
                        <button type="submit">Add Product</button>

                    </form>
                </div>
            }
        />
    );
}

export default ProductAdd;
