import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';

function Reports() {
    const [reportData, setReportData] = useState([]);
    const [selectedPerson, setSelectedPerson] = useState(null);
    const [personDetails, setPersonDetails] = useState([]);

    // Sayfa yüklendiğinde Ana Listeyi (Master) çek
    useEffect(() => {
        fetch("http://localhost:5000/manager/generalReport")
            .then(res => res.json())
            .then(data => { if (data.success) setReportData(data.report); });
    }, []);

    // Satıra tıklandığında Detayları (Detail) çek
    const handlePersonClick = (personId) => {
        setSelectedPerson(personId);
        fetch(`http://localhost:5000/manager/person-orders/${personId}`)
            .then(res => res.json())
            .then(data => { if (data.success) setPersonDetails(data.orders); });
    };



    const downloadPDF = () => {
        try {
            const doc = new jsPDF();

            // PDF Başlığı
            doc.setFontSize(18);
            doc.text("Genel Isletme Raporu", 14, 20);

            // Tarih ekle
            doc.setFontSize(10);
            doc.text(`Rapor Tarihi: ${new Date().toLocaleDateString()}`, 14, 30);

            // Tablo Sütunları ve Verileri
            const columns = ["Ad Soyad", "Rol", "Islem Sayisi", "Toplam Ciro"];
            const rows = reportData.map(p => [
                p.ad_soyad,
                p.rol,
                p.islem_sayisi,
                `${p.toplam_ciro} TL`
            ]);

            // Otomatik Tablo Oluşturma
            doc.autoTable({
                head: [columns],
                body: rows,
                startY: 35,
                theme: 'striped',
                headStyles: { fillColor: [47, 64, 54] } // Senin panel-left rengin (#2f4036)
            });

            doc.save("Genel_Isletme_Raporu.pdf");
        } catch (error) {
            console.error("PDF üretilirken hata oluştu:", error);
            alert("PDF oluşturulamadı. Lütfen konsol kayıtlarını kontrol edin.");
        }
    };
    // ... (importlar ve useEffect kısımları aynı kalıyor)

    return (
        <PanelTemplate
            title="Manager Reports"
            rightContent={
                <div className="panel-right"> {/* panel-right sınıfını ekledik */}
                    <div className="flex justify-between items-center mb-6 w-full" style={{ display: 'flex', justifyContent: 'space-between', width: '100%', marginBottom: '20px' }}>
                        <h1 className="text-2xl font-bold">Genel İşletme Raporu</h1>
                        {/* Senin .panel_button sınıfını buraya ekledik */}
                        <button onClick={downloadPDF} className="panel_button" style={{ width: 'auto' }}>
                            PDF Olarak İndir
                        </button>
                    </div>

                    {/* ANA TABLO (MASTER) */}
                    <div className="w-full">
                        <table className="orders-table"> {/* Senin tablonun stilini buraya bağladık */}
                            <thead>
                                <tr>
                                    <th>Ad Soyad</th>
                                    <th>Rol</th>
                                    <th>İşlem Sayısı</th>
                                    <th>Toplam Ciro</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reportData.map(person => (
                                    <tr
                                        key={person.person_id}
                                        onClick={() => handlePersonClick(person.person_id)}
                                        className="cursor-pointer"
                                        style={{
                                            backgroundColor: selectedPerson === person.person_id ? '#d3e9dd' : 'white',
                                            cursor: 'pointer'
                                        }}
                                    >
                                        <td>{person.ad_soyad}</td>
                                        <td>{person.rol}</td>
                                        <td>{person.islem_sayisi}</td>
                                        <td>{person.toplam_ciro} TL</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>

                    {/* AYRINTI TABLOSU (DETAIL) */}
                    {selectedPerson && (
                        <div style={{ marginTop: '40px', width: '100%', padding: '20px', backgroundColor: '#f9f9f9', borderRadius: '15px' }}>
                            <h2 style={{ marginBottom: '15px', color: '#2f4036' }}>Seçili Kişinin İşlem Detayları</h2>
                            <table className="orders-table"> {/* Detay tablosuna da aynı stili verdik */}
                                <thead>
                                    <tr>
                                        <th>Sipariş ID</th>
                                        <th>Tarih</th>
                                        <th>Tutar</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {personDetails.map(order => (
                                        <tr key={order.order_id}>
                                            <td>{order.order_id}</td>
                                            <td>{order.order_date}</td>
                                            <td>{order.total_amount} TL</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            }
        />
    );
}

export default Reports;
