import React, { useState, useEffect } from "react";
import PanelTemplate from "./PanelTemplate";

function ProductCart() {
    const [cart, setCart] = useState([]);
    const [info, setInfo] = useState(null);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5003/product/list")
            .then(res => res.json())
            .then(data => {
                if (data.success) {
                    setProducts(data.products);
                }
            });
    }, []);

    const productImages = {
        1: "/images/Swal.png",
        2: "/images/scarf1.png",
        3: "/images/Bonnet1.png",
    };

    // info state'ini ve personId'yi daha kararlƒ± hale getirdik
    useEffect(() => {
        const storedInfo = localStorage.getItem("info");
        if (storedInfo) {
            const parsed = JSON.parse(storedInfo);
            setInfo(parsed);

            // üî• KRƒ∞Tƒ∞K D√úZELTME: info okunduƒüu anda sepeti de √ßekelim
            if (parsed.person_id) {
                const key = `cart_${parsed.person_id}`;
                const storedCart = JSON.parse(localStorage.getItem(key)) || [];
                setCart(storedCart);
            }
        }
    }, []);

    // Sepet anahtarƒ± (CartKey)
    const cartkey = info?.person_id ? `cart_${info.person_id}` : null;

    // decreaseQuantity fonksiyonu
    const decreaseQuantity = (productId) => {
        if (!cartkey) return;

        const updatedCart = cart
            .map(item =>
                item.product.product_id === productId
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            )
            .filter(item => item.quantity > 0);

        localStorage.setItem(cartkey, JSON.stringify(updatedCart));
        setCart(updatedCart);
    };

    const clearCart = () => {
        if (!cartkey) return;
        localStorage.removeItem(cartkey);
        setCart([]);
    };

    const placeOrder = async () => {
        // En g√ºncel info'yu her zaman localStorage'dan alalƒ±m
        const currentInfo = JSON.parse(localStorage.getItem("info"));
        const p_id = currentInfo?.person_id;

        if (!p_id) {
            alert("Kullanƒ±cƒ± oturum bilgisi bulunamadƒ±, l√ºtfen tekrar giri≈ü yapƒ±n.");
            return;
        }

        if (cart.length === 0) {
            alert("Sepetiniz bo≈ü.");
            return;
        }

        const totalAmount = cart.reduce(
            (sum, item) => sum + Number(item.product.price) * item.quantity,
            0
        );

        const orderData = {
            customer_id: p_id,
            total_amount: totalAmount,
            items: cart.map(item => ({
                product_id: item.product.product_id,
                quantity: item.quantity,
                price: Number(item.product.price)
            }))
        };

        try {
            const response = await fetch("http://localhost:5003/order/create", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(orderData)
            });

            const data = await response.json();

            if (data.success) {
                alert("Sipari≈ü alƒ±ndƒ±, onay bekliyor.");
                localStorage.removeItem(cartkey);
                setCart([]);
            } else {
                alert("Hata: " + data.message);
            }

        } catch (err) {
            console.error("Order error:", err);
            alert("Sipari≈ü g√∂nderilirken bir hata olu≈ütu.");
        }
    };

    return (
        <PanelTemplate
            title="Product Cart"
            rightContent={
                <div className="cart-layout">
                    <div className="cart-products">
                        {cart.length === 0 && <p>Sepet bo≈ü</p>}
                        <div className="product-gridcart">
                            {cart.map(item => (
                                <div className="product-card" key={item.product.product_id}>
                                    <img
                                        src={productImages[item.product.category_id] || "/images/default.png"}
                                        alt="Product"
                                        className="product-image"
                                    />
                                    <h3>{item.product.product_name}</h3>
                                    <p>{item.product.material}</p>
                                    <p>{item.product.price} ‚Ç∫</p>
                                    <p>Adet: {item.quantity}</p>
                                    <button onClick={() => decreaseQuantity(item.product.product_id)}>
                                        Adet Azalt
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="cart-payment">
                        <h3>Summary</h3>
                        <p>Sum Product: {cart.reduce((sum, i) => sum + i.quantity, 0)}</p>
                        <p>Total Amount: {cart.reduce((sum, i) => sum + i.quantity * Number(i.product.price), 0)} ‚Ç∫</p>
                        {cart.length > 0 && (
                            <>
                                <button className="pay-btn" onClick={placeOrder}>Proceed to payment</button>
                                <button className="clear-btn" onClick={clearCart}>Clear Cart</button>
                            </>
                        )}
                    </div>
                </div>
            }
        />
    );
}

export default ProductCart;
