import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PanelTemplate from "./PanelTemplate";

function Details() {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [logs, setLogs] = useState([]); // Trigger ile tutulan stok loglarÄ±
    const [loading, setLoading] = useState(true);

    // Kategori bazlÄ± varsayÄ±lan gÃ¶rseller (Senin mantÄ±ÄŸÄ±n)
    const productImages = {
        1: "/images/sal.jpg",
        2: "/images/esarp.jpg",
        3: "/images/bone.jpg"
    };

    useEffect(() => {
        // Backend'den hem Ã¼rÃ¼n detaylarÄ±nÄ± hem de loglarÄ± getiren istek
        fetch(`http://localhost:5003/product/${id}`)
            .then((res) => {
                if (!res.ok) throw new Error("Sunucu hatasÄ±: " + res.status);
                return res.json();
            })
            .then((data) => {
                setProduct(data);
                // Backend'den gelen logs dizisini setliyoruz
                if (data.logs) setLogs(data.logs);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Veri Ã§ekme hatasÄ±:", err);
                setLoading(false);
            });
    }, [id]);

    if (loading) return <div style={{ padding: "20px" }}>Veriler HazÄ±rlanÄ±yor...</div>;
    if (!product) return <div style={{ padding: "20px" }}>ÃœrÃ¼n detaylarÄ± yÃ¼klenemedi.</div>;

    return (
        <PanelTemplate
            role="manager"
            rightContent={
                <div style={{ padding: "20px" }}>
                    <h2 style={{ borderBottom: "2px solid #eee", paddingBottom: "10px", color: "#333" }}>
                        ÃœrÃ¼n Detay SayfasÄ± (AyrÄ±ntÄ± Formu)
                    </h2>

                    <div style={{ display: "flex", gap: "40px", marginTop: "20px", flexWrap: "wrap" }}>

                        {/* SOL TARAF: ÃœrÃ¼n GÃ¶rseli (Senin verdiÄŸin img mantÄ±ÄŸÄ±) */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <img
                                src={product.image_url && product.image_url !== "/images/default.png"
                                    ? product.image_url
                                    : (productImages[product.category_id] || "/images/default.png")}
                                alt={`${product.material} ${product.category_name}`}
                                style={{
                                    width: "100%",
                                    borderRadius: "12px",
                                    boxShadow: "0 6px 18px rgba(0,0,0,0.1)",
                                    maxHeight: "480px",
                                    objectFit: "cover"
                                }}
                                onError={(e) => { e.target.src = "/images/default.png"; }}
                            />
                        </div>

                        {/* SAÄž TARAF: ÃœrÃ¼n Bilgileri (View'dan Gelenler) */}
                        <div style={{ flex: "1", minWidth: "300px" }}>
                            <div style={{ backgroundColor: "#fcfcfc", padding: "25px", borderRadius: "15px", border: "1px solid #efefef" }}>
                                <h3 style={{ marginTop: 0, color: "#444" }}>{product.category_name} - {product.material}</h3>
                                <hr style={{ border: "0.5px solid #eee" }} />

                                <p><strong>Stok Kodu:</strong> #{product.product_id}</p>
                                <p><strong>Renk SeÃ§eneÄŸi:</strong> {product.color}</p>
                                <p><strong>Mevcut Stok:</strong> {product.quantity} Adet</p>

                                <div style={{ margin: "20px 0" }}>
                                    <span style={{ fontSize: "28px", color: "#2e7d32", fontWeight: "bold" }}>
                                        {product.price} TL
                                    </span>
                                </div>

                                <button style={{
                                    width: "100%",
                                    padding: "16px",
                                    backgroundColor: "#ff9800",
                                    color: "white",
                                    border: "none",
                                    borderRadius: "8px",
                                    cursor: "pointer",
                                    fontSize: "1.2em",
                                    fontWeight: "bold"
                                }}>
                                    Sepete Ekle
                                </button>
                            </div>

                            <p style={{ marginTop: "15px", fontSize: "0.8em", color: "#999" }}>
                                * Bu sayfa veritabanÄ±ndaki <b>view_product_details</b> gÃ¶rÃ¼nÃ¼mÃ¼nÃ¼ (View) kullanmaktadÄ±r.
                            </p>
                        </div>
                    </div>

                    {/* --- ALT KISIM: TRIGGER (STOK LOGLARI) --- */}
                    <div style={{ marginTop: "50px" }}>
                        <h3 style={{ color: "#d32f2f" }}>ðŸ“¦ VeritabanÄ± Ä°ÅŸlem GÃ¼nlÃ¼ÄŸÃ¼ (Trigger Logs)</h3>
                        <p style={{ fontSize: "0.9em", color: "#666" }}>ÃœrÃ¼n Ã¼zerinde yapÄ±lan stok deÄŸiÅŸimleri otomatik olarak kaydedilir.</p>

                        <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "15px", boxShadow: "0 4px 12px rgba(0,0,0,0.05)" }}>
                            <thead>
                                <tr style={{ backgroundColor: "#f4f4f4" }}>
                                    <th style={{ padding: "12px", border: "1px solid #ddd", textAlign: "left" }}>Ä°ÅŸlem Tipi</th>
                                    <th style={{ padding: "12px", border: "1px solid #ddd", textAlign: "left" }}>Eski Stok</th>
                                    <th style={{ padding: "12px", border: "1px solid #ddd", textAlign: "left" }}>Yeni Stok</th>
                                    <th style={{ padding: "12px", border: "1px solid #ddd", textAlign: "left" }}>Tarih</th>
                                </tr>
                            </thead>
                            <tbody>
                                {logs.length > 0 ? logs.map((log, index) => (
                                    <tr key={index} style={{ borderBottom: "1px solid #eee" }}>
                                        <td style={{ padding: "12px", border: "1px solid #ddd" }}>{log.operation_type}</td>
                                        <td style={{ padding: "12px", border: "1px solid #ddd" }}>{log.old_quantity}</td>
                                        <td style={{ padding: "12px", border: "1px solid #ddd", fontWeight: "bold" }}>{log.new_quantity}</td>
                                        <td style={{ padding: "12px", border: "1px solid #ddd" }}>{new Date(log.change_date).toLocaleString()}</td>
                                    </tr>
                                )) : (
                                    <tr>
                                        <td colSpan="4" style={{ padding: "20px", textAlign: "center", color: "#999" }}>
                                            Bu Ã¼rÃ¼ne ait henÃ¼z bir iÅŸlem geÃ§miÅŸi bulunmamaktadÄ±r.
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            }
        />
    );
}

export default Details;
