import React, { useEffect, useState } from "react";
import PanelTemplate from "./PanelTemplate";
import { useNavigate } from "react-router-dom";

function Orders() {
    const navigate = useNavigate();
    const git = (page) => {
        navigate(`${page}`)
    }

    const [orders, setOrders] = useState([]);

    const [formData, setFormData] = useState({
        order_id: "",
        customer_id: "",
        employee_id: "",
        order_date: "",
        total_amount: ""
    });

    useEffect(() => {
        fetch("http://localhost:5003/order/pending")
            .then(res => res.json())
            .then(data => {
                if (data.success)
                    setOrders(data.orders);
            });
    }, []);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch("http://localhost:5003/order/confirm", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData),
            });
        }
        catch (err) {
            console.error("Order confirmation error:", err);
            alert("An error occurred during order confirmation. Please try again later.");
        }
    };

    return (
        <PanelTemplate
            title="Orders"
            rightContent={
                <div>
                    {orders.length === 0 && (<p>No pending orders.</p>)}

                    <table border="1" style={{ width: "100%", marginTop: "20px", textAlign: "center" }}>
                        <thead>
                            <tr>
                                <th>Pending Order ID</th>
                                <th>customer ID</th>
                                <th>Order Date</th>
                                <th>Total Amount</th>
                            </tr>
                        </thead>

                        <tbody>
                            {orders.map(order => (
                                <tr key={order.customer_id}>
                                    <td>{order.pending_order_id}</td>
                                    <td>{order.customer_id}</td>
                                    <td>{order.order_date}</td>
                                    <td>{order.total_amount}</td>

                                    <td>
                                        <button
                                            style={{
                                                backgroundColor: "#c50b0bff",
                                                color: "white",
                                                padding: "8px 20px",
                                                borderRadius: "8px",
                                                border: "none",
                                                cursor: "pointer"
                                            }} onChange={handleChange}
                                        >
                                            Onayla
                                        </button>
                                    </td>
                                </tr>

                            ))}
                        </tbody>
                    </table>

                </div >
            }
        />
    );
}

export default Orders;
